*/
contract DAO is State, Bonding, Market, Regulator, Govern {
    using SafeMath for uint256;

    event Advance(uint256 indexed epoch, uint256 block, uint256 timestamp);
    event Incentivization(address indexed account, uint256 amount);

    function initialize() initializer public {
        // Reward deployer
        mintToAccount(msg.sender, Constants.getAdvanceIncentive());

        // bootstrap rewards
        mintToAccount(pool(), Constants.getBootstrappingPoolReward());
        StakingRewards(pool()).notifyRewardAmount(Constants.getBootstrappingPoolReward());

        // team
        mintToAccount(msg.sender, 1000e18);
    }

    function advanceEpoch() external {
        uint256 advanceIncentive;
        if (epoch() < Constants.getLowAdvanceRewardsPeriod()) {
            advanceIncentive = Constants.getLowAdvanceIncentive();
        } else {
            advanceIncentive = Constants.getAdvanceIncentive();
        }

        incentivize(msg.sender, advanceIncentive);

        Bonding.step();
        Regulator.step();
        Market.step();

        emit Advance(epoch(), block.number, block.timestamp);
    }

    function incentivize(address account, uint256 amount) private {
        mintToAccount(account, amount);
        emit Incentivization(account, amount);
    }
}
