/**
 * @dev A token holder contract that will allow a beneficiary to extract the
 * tokens after a given release time.
 *
 * Useful for simple vesting schedules like "advisors get all of their tokens
 * after 1 year".
 */
contract TokenTimelock is Ownable{
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // ERC20 basic token contract being held
    IERC20 private _token;

    // beneficiary of tokens after they are released
    address private _beneficiary;

    // timestamp when token release is enabled
    uint256 private _initialReleaseDuration;
    uint256 private _releaseDuration;
    uint256 private _startTime;
    uint256 private _releasedAmount;
    uint256 private _milestoneCount;
    uint256 private _lockedAmount;

    constructor (IERC20 token_, address beneficiary_) public{
        _token = token_;
        _beneficiary = beneficiary_;
        _initialReleaseDuration = 60 days;
        _releaseDuration = 180 days;
        _startTime = block.timestamp;
        _milestoneCount = 6;
    }

    function updateLockedAmount() public onlyOwner{
        require(_lockedAmount == 0);
        _lockedAmount = _token.balanceOf(address(this));
    }
    
    /**
     * @return the token being held.
     */
    function token() public view returns (IERC20) {
        return _token;
    }

    /**
     * @return the beneficiary of the tokens.
     */
    function beneficiary() public view returns (address) {
        return _beneficiary;
    }

    function startTime() public view returns (uint256) {
        return _startTime;
    }

    function releasedAmount() public view returns (uint256) {
        return _releasedAmount;
    }

    function lockedAmount() public view returns (uint256) {
        return _lockedAmount;
    }

    /**
     * @notice Transfers tokens held by timelock to beneficiary.
     */
    function release() public virtual {
        // solhint-disable-next-line not-rely-on-time
        require(_token.balanceOf(address(this)) > 0);
        require(_lockedAmount > 0);

        uint256 amount;
        
        if(block.timestamp >= (_startTime.add(_initialReleaseDuration))){
             amount = amount.add(_lockedAmount.div(_milestoneCount));
        }
        
        if(block.timestamp >= (_startTime.add(_initialReleaseDuration).add(_releaseDuration))){
             amount = amount.add(_lockedAmount.div(_milestoneCount));
        }
        
        if(block.timestamp >= (_startTime.add(_initialReleaseDuration).add(_releaseDuration.mul(2)))){
             amount = amount.add(_lockedAmount.div(_milestoneCount));
        }
        
        if(block.timestamp >= (_startTime.add(_initialReleaseDuration).add(_releaseDuration.mul(3)))){
             amount = amount.add(_lockedAmount.div(_milestoneCount));
        }

        if(block.timestamp >= (_startTime.add(_initialReleaseDuration).add(_releaseDuration.mul(4)))){
             amount = amount.add(_lockedAmount.div(_milestoneCount));
        }

        if(block.timestamp >= (_startTime.add(_initialReleaseDuration).add(_releaseDuration.mul(5)))){
             amount = amount.add(_lockedAmount.div(_milestoneCount));
        }
        
        amount = amount.sub(_releasedAmount);
        require(amount > 0, "TokenTimelock: no tokens to release");
        
        _token.safeTransfer(_beneficiary, amount);
        _releasedAmount = _releasedAmount.add(amount);

    }
}
