pragma solidity ^0.5.0;

contract Bridge is Ownable{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    
    event Send2Bridge(address indexed user, uint256 amount);
    event Send2User(address indexed user, uint256 amount, string hash);
    event WithdrawTops(address indexed toAddress, uint256 amount);
    
    IERC20 public tops = IERC20(0xF5a04b9E798892e96dE68733AD8FFedDa39B7E5A);
    mapping(string => bool) public records;
    uint256 public limit = 5000000000;
    
    function send2Bridge(uint256 amount) public {
        require(amount >= limit, "amount must greater than limit");
        tops.safeTransferFrom(msg.sender, address(this), amount);
        emit Send2Bridge(msg.sender, amount);
    }
    
    function setLimit(uint256 newLimit) public onlyOwner{
        limit = newLimit;
    }
    
    function send2User(address user, uint256 amount, string memory hash) public onlyOwner {
        if(!records[hash]){
            tops.safeTransfer(user, amount);
            emit Send2User(user, amount, hash);
            records[hash] = true;
        }
    }
    
    function withdrawTops(address toAddress) public onlyOwner{
        uint256 balance = tops.balanceOf(address(this));
        tops.safeTransfer(toAddress, balance);
        emit WithdrawTops(toAddress, balance);
        
    }
}
