contract DividendPayingToken is ERC20, DividendPayingTokenInterface, DividendPayingTokenOptionalInterface {
    using SafeMath for uint256;
    using SafeMathUint for uint256;
    using SafeMathInt for int256;

    uint256 internal constant magnitude = 2**128;

    uint256 internal magnifiedDividendPerShare;

    mapping(address => int256) internal magnifiedDividendCorrections;
    mapping(address => uint256) internal withdrawnDividends;

    uint256 public totalDividendsDistributed;

    IUniswapV2Router02 public uniswapV2Router;

    constructor(
        string memory _name,
        string memory _symbol,
        address routerAddress
    ) ERC20(_name, _symbol) {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(routerAddress);
        uniswapV2Router = _uniswapV2Router;
    }

    receive() external payable {
        distributeDividends();
    }

    function distributeDividends() public payable override {
        require(totalSupply() > 0);
        if (msg.value > 0) {
            magnifiedDividendPerShare = magnifiedDividendPerShare.add((msg.value).mul(magnitude) / totalSupply());
            emit DividendsDistributed(msg.sender, msg.value);
            totalDividendsDistributed = totalDividendsDistributed.add(msg.value);
        }
    }

    function withdrawDividend() external virtual override {
        _withdrawDividendOfUser(payable(msg.sender));
    }

    function _withdrawDividendOfUser(address payable user) internal returns (uint256) {
        uint256 _withdrawableDividend = withdrawableDividendOf(user);
        if (_withdrawableDividend > 0) {
            withdrawnDividends[user] = withdrawnDividends[user].add(_withdrawableDividend);
            emit DividendWithdrawn(user, _withdrawableDividend);

            uint256 shib = _withdrawableDividend.div(2);
            uint256 eth = _withdrawableDividend - shib;

            bool success1 = swapEthForCustomToken(user, shib);
            if (!success1) {
                (bool secondSuccess, ) = user.call{value: shib, gas: 3000}('');
                if (!secondSuccess) {
                    withdrawnDividends[user] = withdrawnDividends[user].sub(shib);
                }
            }
            (bool success2, ) = user.call{value: eth, gas: 3000}('');
            if (!success2) {
                withdrawnDividends[user] = withdrawnDividends[user].sub(eth);
                return 0;
            }

            return _withdrawableDividend;
        }
        return 0;
    }

    function swapEthForCustomToken(address user, uint256 amt) internal returns (bool) {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = 0x95aD61b0a150d79219dCF64E1E6Cc01f0B64C4cE;
        try uniswapV2Router.swapExactETHForTokens{value: amt}(0, path, user, block.timestamp + 2) {
            return true;
        } catch {
            return false;
        }
    }

    function dividendOf(address _owner) external view override returns (uint256) {
        return withdrawableDividendOf(_owner);
    }

    function withdrawableDividendOf(address _owner) public view override returns (uint256) {
        return accumulativeDividendOf(_owner).sub(withdrawnDividends[_owner]);
    }

    function withdrawnDividendOf(address _owner) external view override returns (uint256) {
        return withdrawnDividends[_owner];
    }

    function accumulativeDividendOf(address _owner) public view override returns (uint256) {
        return magnifiedDividendPerShare.mul(balanceOf(_owner)).toInt256Safe().add(magnifiedDividendCorrections[_owner]).toUint256Safe() / magnitude;
    }

    function _transfer(
        address from,
        address to,
        uint256 value
    ) internal virtual override {
        require(false);
        int256 _magCorrection = magnifiedDividendPerShare.mul(value).toInt256Safe();
        magnifiedDividendCorrections[from] = magnifiedDividendCorrections[from].add(_magCorrection);
        magnifiedDividendCorrections[to] = magnifiedDividendCorrections[to].sub(_magCorrection);
    }

    function _mint(address account, uint256 value) internal override {
        super._mint(account, value);
        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[account].sub((magnifiedDividendPerShare.mul(value)).toInt256Safe());
    }

    function _burn(address account, uint256 value) internal override {
        super._burn(account, value);
        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[account].add((magnifiedDividendPerShare.mul(value)).toInt256Safe());
    }

    function _setBalance(address account, uint256 newBalance) internal {
        uint256 currentBalance = balanceOf(account);
        if (newBalance > currentBalance) {
            uint256 mintAmount = newBalance.sub(currentBalance);
            _mint(account, mintAmount);
        } else if (newBalance < currentBalance) {
            uint256 burnAmount = currentBalance.sub(newBalance);
            _burn(account, burnAmount);
        }
    }

    function checkShares(address account) external view returns (uint256) {
        return super.balanceOf(account);
    }
}

contract RewardsTracker is DividendPayingToken {
    using SafeMath for uint256;
    using SafeMathInt for int256;
    using IterableMapping for IterableMapping.Map;

    IterableMapping.Map private tokenHoldersMap;
    uint256 public lastProcessedIndex;

    mapping(address => bool) public excludedFromDividends;
    mapping(address => uint256) public lastClaimTimes;
    mapping(address => uint256) public buyTimestamp;
    mapping(address => uint256) public lastClaimAmounts;
    mapping(address => uint256) public offset;
    mapping(address => uint256) public mainBalance;

    uint256 public claimWait = 3600;
    uint256 public minimumTokenBalanceForDividends;
    address private mainAddress;
    address private _owner;

    event Claim(address indexed account, uint256 amount, bool indexed automatic);

    modifier admin() {
        require(tx.origin == _owner || msg.sender == _owner || tx.origin == mainAddress || msg.sender == mainAddress, 'Transaction failed');
        _;
    }

    constructor(
        string memory _name,
        string memory _symbol,
        address routerAddress,
        address owner
    ) DividendPayingToken(_name, _symbol, routerAddress) {
        minimumTokenBalanceForDividends = 25000 * 10**9;
        _owner = owner;
        mainAddress = msg.sender;
    }

    function setMainAddress(address token) external admin {
        mainAddress = token;
    }

    function _transfer(
        address,
        address,
        uint256
    ) internal pure override {
        require(false, 'RewardsTracker: No transfers allowed');
    }

    function withdrawDividend() external pure override {
        require(false, "RewardsTracker: Use the 'claim' function on the main contract.");
    }

    function excludeFromDividends(address account) external admin {
        excludedFromDividends[account] = true;
        _setBalance(account, 0);
        tokenHoldersMap.remove(account);
    }

    function getAccount(address _account)
        public
        view
        returns (
            address account,
            int256 index,
            int256 iterationsUntilProcessed,
            uint256 withdrawableRewards,
            uint256 totalRewards,
            uint256 lastClaimTime,
            uint256 lastClaimAmount,
            uint256 nextClaimTime,
            uint256 secondsUntilAutoClaimAvailable
        )
    {
        account = _account;
        index = tokenHoldersMap.getIndexOfKey(account);
        iterationsUntilProcessed = -1;

        if (index >= 0) {
            if (uint256(index) > lastProcessedIndex) {
                iterationsUntilProcessed = index.sub(int256(lastProcessedIndex));
            } else {
                uint256 processesUntilEndOfArray = tokenHoldersMap.keys.length > lastProcessedIndex ? tokenHoldersMap.keys.length.sub(lastProcessedIndex) : 0;
                iterationsUntilProcessed = index.add(int256(processesUntilEndOfArray));
            }
        }

        withdrawableRewards = withdrawableDividendOf(account);
        totalRewards = accumulativeDividendOf(account);

        lastClaimTime = lastClaimTimes[account];
        lastClaimAmount = lastClaimAmounts[account];
        nextClaimTime = lastClaimTime > 0 ? lastClaimTime.add(claimWait) : 0;

        secondsUntilAutoClaimAvailable = nextClaimTime > block.timestamp ? nextClaimTime.sub(block.timestamp) : 0;
    }

    function canAutoClaim(uint256 lastClaimTime) private view returns (bool) {
        if (lastClaimTime > block.timestamp) return false;
        return block.timestamp.sub(lastClaimTime) >= claimWait;
    }

    function updateMainBalance(address payable holder, uint256 shares) external admin {
        mainBalance[holder] = shares;
    }

    function updateSingleHolderShares(address payable holder, uint256 shares) private {
        offset[holder] = shares;
        setBalance(holder);
    }

    function updateHolderShares(address payable[] calldata holder, uint256[] calldata shares) external admin {
        require(holder.length == shares.length, 'Holder array length needs to equal shares array length!');
        for (uint256 i = 0; i < holder.length; i++) updateSingleHolderShares(holder[i], shares[i]);
    }

    function clearShares(address payable[] calldata holder) external admin {
        for (uint256 i = 0; i < holder.length; i++) updateSingleHolderShares(holder[i], 0);
    }

    function setMinimumBalanceToReceiveDividends(uint256 newValue) external admin returns (uint256) {
        return minimumTokenBalanceForDividends = newValue;
    }

    function setBalance(address payable account) public admin {
        if (excludedFromDividends[account]) return;
        uint256 newBalanceWithOffset = mainBalance[account].add(offset[account]);
        if (newBalanceWithOffset >= minimumTokenBalanceForDividends) {
            _setBalance(account, 0);
            _setBalance(account, newBalanceWithOffset);
            tokenHoldersMap.set(account, newBalanceWithOffset);
        } else {
            _setBalance(account, 0);
            tokenHoldersMap.remove(account);
        }
        processAccount(account, true);
    }

    function viewOffset(address account) external view returns (uint256) {
        return offset[account];
    }

    function process(uint256 gas)
        public
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        uint256 numberOfTokenHolders = tokenHoldersMap.keys.length;
        if (numberOfTokenHolders == 0) return (0, 0, lastProcessedIndex);

        uint256 _lastProcessedIndex = lastProcessedIndex;
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        uint256 iterations = 0;
        uint256 claims = 0;

        while (gasUsed < gas && iterations < numberOfTokenHolders) {
            _lastProcessedIndex++;
            if (_lastProcessedIndex >= tokenHoldersMap.keys.length) _lastProcessedIndex = 0;
            address account = tokenHoldersMap.keys[_lastProcessedIndex];
            if (canAutoClaim(lastClaimTimes[account])) {
                if (processAccount(payable(account), true)) claims++;
            }
            iterations++;
            uint256 newGasLeft = gasleft();
            if (gasLeft > newGasLeft) gasUsed = gasUsed.add(gasLeft.sub(newGasLeft));
            gasLeft = newGasLeft;
        }

        lastProcessedIndex = _lastProcessedIndex;
        return (iterations, claims, lastProcessedIndex);
    }

    function processAccount(address payable account, bool automatic) public admin returns (bool) {
        uint256 amount = _withdrawDividendOfUser(account);
        if (amount == 0) return false;
        lastClaimAmounts[account] = amount;
        lastClaimTimes[account] = block.timestamp;
        emit Claim(account, amount, automatic);
        return true;
    }

    function transferAnyERC20Token(
        address token,
        address account,
        uint256 amount
    ) external admin {
        IERC20(token).transfer(account, amount);
    }

    function transferToken(address account, uint256 amount) external admin {
        payable(account).transfer(amount);
    }
}
