        return string(abi.encodePacked(
            baseURIx,
            Strings.toString(_id),
            ".json")
        );
    }

    function enablePublicMint() public onlyOwner {
      publicMint = true;
    }

    function extractEther() external onlyOwner {
      payable(msg.sender).transfer(address(this).balance);
    }
}

