// File: contracts\strategies\StrategyTUSDCurve.txt

// SPDX-License-Identifier: MIT

pragma solidity ^0.5.17;

//import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
//import "@openzeppelin/contracts/math/SafeMath.sol";
//import "@openzeppelin/contracts/utils/Address.sol";







contract StrategyTUSDCurve {
    using SafeERC20 for IERC20;
    using Address for address;
    using SafeMath for uint256;

    address public constant want = address(0x0000000000085d4780B73119b644AE5ecd22b376);
    address public constant k = address(0xd5A8a6ac12C8B87c40336F4e034084355943284f);
    address public constant kcrv = address(0x02336aB947f189baD0a194a977c4300146545a53);
    address public constant kkcrv = address(0x007803d3f7db6b86Ca17a9Cf0A02850Beb0EE430);
    address public constant curve = address(0xd8B4cd27294aeB5CFFfa24bcAA9432E7d19b71dA);

    address public constant dai = address(0x6B175474E89094C44Da98b954EedeAC495271d0F);
    address public constant kdai = address(0xb6Fb70eB57189302ef4A37741a301f23c32025ec);

    address public constant usdc = address(0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48);
    address public constant kusdc = address(0xdA6a06327c7A9650b78c69360A45494159FA63de);

    address public constant usdt = address(0xdAC17F958D2ee523a2206206994597C13D831ec7);
    address public constant kusdt = address(0x51E810608045C013F8F9240F426DAe36690736fd);

    address public constant tusd = address(0x0000000000085d4780B73119b644AE5ecd22b376);
    address public constant ktusd = address(0xd5A8a6ac12C8B87c40336F4e034084355943284f);

    address public governance;
    address public controller;

    constructor(address _controller) public {
        governance = msg.sender;
        controller = _controller;
    }

    function getName() external pure returns (string memory) {
        return "StrategyTUSDCurve";
    }

    function deposit() public {
        uint256 _want = IERC20(want).balanceOf(address(this));
        if (_want > 0) {
            IERC20(want).safeApprove(k, 0);
            IERC20(want).safeApprove(k, _want);
            kERC20(k).deposit(_want);
        }
        uint256 _k = IERC20(k).balanceOf(address(this));
        if (_k > 0) {
            IERC20(k).safeApprove(curve, 0);
            IERC20(k).safeApprove(curve, _k);
            ICurveFi(curve).add_liquidity([0, 0, 0, _k], 0);
        }
        uint256 _kcrv = IERC20(kcrv).balanceOf(address(this));
        if (_kcrv > 0) {
            IERC20(kcrv).safeApprove(kkcrv, 0);
            IERC20(kcrv).safeApprove(kkcrv, _kcrv);
            kERC20(kkcrv).deposit(_kcrv);
        }
    }

    // Controller only function for creating additional rewards from dust
    function withdraw(IERC20 _asset) external returns (uint256 balance) {
        require(msg.sender == controller, "!controller");
        require(want != address(_asset), "want");
        require(k != address(_asset), "k");
        require(kcrv != address(_asset), "kcrv");
        require(kkcrv != address(_asset), "kkcrv");
        balance = _asset.balanceOf(address(this));
        _asset.safeTransfer(controller, balance);
    }

    // Withdraw partial funds, normally used with a vault withdrawal
    function withdraw(uint256 _amount) external {
        require(msg.sender == controller, "!controller");
        uint256 _balance = IERC20(want).balanceOf(address(this));
        if (_balance < _amount) {
            _amount = _withdrawSome(_amount.sub(_balance));
            _amount = _amount.add(_balance);
        }

        address _vault = IController(controller).vaults(address(want));
        require(_vault != address(0), "!vault"); // additional protection so we don't burn the funds
        IERC20(want).safeTransfer(_vault, _amount);
    }

    // Withdraw all funds, normally used when migrating strategies
    function withdrawAll() external returns (uint256 balance) {
        require(msg.sender == controller, "!controller");
        _withdrawAll();

        balance = IERC20(want).balanceOf(address(this));

        address _vault = IController(controller).vaults(address(want));
        require(_vault != address(0), "!vault"); // additional protection so we don't burn the funds
        IERC20(want).safeTransfer(_vault, balance);
    }

    function withdrawTUSD(uint256 _amount) internal returns (uint256) {
        IERC20(kcrv).safeApprove(curve, 0);
        IERC20(kcrv).safeApprove(curve, _amount);
        ICurveFi(curve).remove_liquidity(_amount, [uint256(0), 0, 0, 0]);

        uint256 _kdai = IERC20(kdai).balanceOf(address(this));
        uint256 _kusdc = IERC20(kusdc).balanceOf(address(this));
        uint256 _kusdt = IERC20(kusdt).balanceOf(address(this));

        if (_kdai > 0) {
            IERC20(kdai).safeApprove(curve, 0);
            IERC20(kdai).safeApprove(curve, _kdai);
            ICurveFi(curve).exchange(0, 3, _kdai, 0);
        }
        if (_kusdc > 0) {
            IERC20(kusdc).safeApprove(curve, 0);
            IERC20(kusdc).safeApprove(curve, _kusdc);
            ICurveFi(curve).exchange(1, 3, _kusdc, 0);
        }
        if (_kusdt > 0) {
            IERC20(kusdt).safeApprove(curve, 0);
            IERC20(kusdt).safeApprove(curve, _kusdt);
            ICurveFi(curve).exchange(2, 3, _kusdt, 0);
        }

        uint256 _before = IERC20(want).balanceOf(address(this));
        kERC20(ktusd).withdraw(IERC20(ktusd).balanceOf(address(this)));
        uint256 _after = IERC20(want).balanceOf(address(this));

        return _after.sub(_before);
    }

    function _withdrawAll() internal {
        uint256 _kkcrv = IERC20(kkcrv).balanceOf(address(this));
        if (_kkcrv > 0) {
            kERC20(kkcrv).withdraw(_kkcrv);
            withdrawTUSD(IERC20(kcrv).balanceOf(address(this)));
        }
    }

    function _withdrawSome(uint256 _amount) internal returns (uint256) {
        // calculate amount of kcrv to withdraw for amount of _want_
        uint256 _kcrv = _amount.mul(1e18).div(ICurveFi(curve).get_virtual_price());
        // calculate amount of kkcrv to withdraw for amount of _kcrv_
        uint256 _kkcrv = _kcrv.mul(1e18).div(kERC20(kkcrv).getPricePerFullShare());
        uint256 _before = IERC20(kcrv).balanceOf(address(this));
        kERC20(kkcrv).withdraw(_kkcrv);
        uint256 _after = IERC20(kcrv).balanceOf(address(this));
        return withdrawTUSD(_after.sub(_before));
    }

    function balanceOfWant() public view returns (uint256) {
        return IERC20(want).balanceOf(address(this));
    }

    function balanceOfKKCRV() public view returns (uint256) {
        return IERC20(kkcrv).balanceOf(address(this));
    }

    function balanceOfKKCRVinKCRV() public view returns (uint256) {
        return balanceOfKKCRV().mul(kERC20(kkcrv).getPricePerFullShare()).div(1e18);
    }

    function balanceOfKKCRVinkTUSD() public view returns (uint256) {
        return balanceOfKKCRVinKCRV().mul(ICurveFi(curve).get_virtual_price()).div(1e18);
    }

    function balanceOfKCRV() public view returns (uint256) {
        return IERC20(kcrv).balanceOf(address(this));
    }

    function balanceOfKCRVkTUSD() public view returns (uint256) {
        return balanceOfKCRV().mul(ICurveFi(curve).get_virtual_price()).div(1e18);
    }

    function balanceOf() public view returns (uint256) {
        return balanceOfWant().add(balanceOfKKCRVinkTUSD());
    }

    function setGovernance(address _governance) external {
        require(msg.sender == governance, "!governance");
        governance = _governance;
    }

    function setController(address _controller) external {
        require(msg.sender == governance, "!governance");
        controller = _controller;
    }
}
