	mapping(address => uint256) promotion_map;
	address[] promoter_list;

	function getPromoterList() external view returns (address[] memory) {

		address[] memory r = new address[](promoter_list.length);
		for (uint256 i = 0; i < promoter_list.length; i++) {
			r[i] = promoter_list[i];
		}

		return r;
        }

	function getPromoterCounter(address promoter_address) public view returns(uint256) {
		return promotion_map[promoter_address];
	}

	function mintByRecommendation(uint256 _batchCount, address promoter_address) payable public {

		if (promotion_map[promoter_address] == 0) { // new promoter
			// add to promoter list
			promoter_list.push(promoter_address);
		}

		promotion_map[promoter_address] += _batchCount;				
	
		mint(_batchCount);
	}


