	receive() external payable {
		emit Received(msg.sender, msg.value);
	}

	function setBaseURI(string memory _newURI) public onlyOwner {
		baseURI = _newURI;
	}

	function setTokenURI(uint256 _tokenId, string memory _tokenURI) public onlyOwner {
		_setTokenURI(_tokenId, _tokenURI);
	}
  
	function allowMinting(bool _isMintingAllowed) public onlyOwner {
		isMintingAllowed = _isMintingAllowed;
	}

	function mint(uint256 _batchCount) payable public {
		require(isMintingAllowed, "minting not allowed");
		require(_batchCount > 0 && _batchCount <= maxBatch, "requested more as allowed");
		require(_count_minted_A + _count_minted_B < totalCount, "all NFTs minted");
		require(_count_minted_A + _count_minted_B + _batchCount <= totalCount, "requested more than available");
		require(msg.value == _batchCount * _getNFTPrice(), "insufficient ether sent");
		require(balanceOf(msg.sender) + _batchCount <= maxBatch, "Personal mint amount exceeded");

		for(uint256 i=0; i< _batchCount; i++){
			uint256 index = _getNextCryptopIndex(false);
			if (index == 0) {
				revert("couldn't mint");
			}
			_safeMint(_msgSender(), index);
		}

		_walletDistro();
	}

