contract BuyBack is Ownable{

     using SafeMath for uint256;

     IUniswap uniswap;

    constructor(address _uniswap) public {
        uniswap = IUniswap(_uniswap);
    }

    function swapExactETHForTokens(
        uint amountOut,
        address token,
        uint deadline
    ) external payable {
        address[] memory path = new address[](2);
        path[0] = uniswap.WETH();
        path[1] = token;
        uniswap.swapExactETHForTokens{value: msg.value}(
            amountOut * 1e18,
            path,
            address(this),
            deadline + block.timestamp
        );
    }   



     function sendAndBurnToken(uint256 value,  address burn,  address reward, IERC20 buyBackToken ) onlyOwner external {
     require(value != 0);
	 buyBackToken.transfer(burn, value/5);
     buyBackToken.transfer(reward, (value/5)*4);
  }
}
