// File: contracts/Vote.sol

pragma solidity ^0.6.0;




contract Vote is Ownable {
    using SafeMath for uint256;
    
    // Option Struct
    struct Option {
        uint256 voted;
        string text;
    }
    // Option List
    mapping(uint256 => Option) public options;
    // Option Size
    uint256 public optionSize;
    
    // Vote Struct
    struct Vote {
        address voter;
        uint256 option;
    }
    // Vote List
    mapping(uint256 => Vote) public votes;
    // Vote Size
    uint256 public voteSize;

    // IsVoted
    mapping(address => bool) public isVoted;

    address public token;
    bool public isVoting;

    constructor(address _token) public {
        optionSize = 0;
        voteSize = 0;
        isVoting = false;
        token = _token;
    }

    function setToken(address _token) public onlyOwner {
        token = _token;
    }

    function openVoting() public onlyOwner {
        isVoting = true;
    }

    function closeVoting() public onlyOwner {
        isVoting = false;
        for(uint256 i=0;i<voteSize;i++) {
            uint256 option = votes[i].option;
            uint256 amount = IERC20(token).balanceOf(votes[i].voter);
            options[option].voted = options[option].voted + amount;
        }
    }

    function addOption(string memory _text) public onlyOwner {
        options[optionSize] = Option(0, _text);
        optionSize = optionSize + 1;
    }

    function vote(uint256 id) public {
        require(isVoting, "::vote:: Voting is not started");
        require(0 <= id && id < optionSize, "::vote:: Voting ID is invalid!");
        if(isVoted[msg.sender]) {
            for(uint256 i=0;i<voteSize;i++) {
                if(msg.sender == votes[i].voter) {
                    votes[i] = Vote(msg.sender, id);
                    break;
                }
            }
        } else {
            isVoted[msg.sender] = true;
            votes[voteSize] = Vote(msg.sender, id);
            voteSize = voteSize + 1;
        }
    }


    function position(address addr) public view returns(uint256 option) {
        for(uint256 i=0;i<voteSize;i++) {
            if(votes[i].voter == addr) {
                return votes[i].option;
            }
        }   
        return 999999;
    }

}
