    function getAlpaca(uint256 _id)
        external
        view
        returns (
            uint256 id,
            bool isReady,
            uint256 cooldownEndBlock,
            uint256 birthTime,
            uint256 matronId,
            uint256 sireId,
            uint256 hatchingCost,
            uint256 hatchingCostMultiplier,
            uint256 hatchCostMultiplierEndBlock,
            uint256 generation,
            uint256 gene,
            uint256 energy,
            uint256 state
        );

    function hasPermissionToBreedAsSire(address _addr, uint256 _id)
        external
        view
        returns (bool);

    function grandPermissionToBreed(address _addr, uint256 _sireId) external;

    function clearPermissionToBreed(uint256 _alpacaId) external;

    function hatch(uint256 _matronId, uint256 _sireId)
        external
        payable
        returns (uint256);

    function crack(uint256 _id) external;
}


// Root file: contracts/AlpacaRecycleBox/AlpacaRecycleBox.sol


pragma solidity 0.6.12;

// import "@openzeppelin/contracts/math/SafeMath.sol";
// import "@openzeppelin/contracts/access/Ownable.sol";
// import "@openzeppelin/contracts/token/ERC1155/ERC1155Receiver.sol";

// import "contracts/interfaces/IWoolToken.sol";
// import "contracts/interfaces/IWoolController.sol";
// import "contracts/interfaces/ICryptoAlpaca.sol";

contract AlpacaRecycleBox is Ownable, ERC1155Receiver {
    using SafeMath for uint256;

    /* ========== STATES ========== */

    // The ALPA ERC20 token
    IWoolToken public wool;

    // Wool controller
    IWoolController public woolController;

    // Crypto alpaca contract
    ICryptoAlpaca public cryptoAlpaca;

    // number of recycled alpaca
    uint256 public totalRecycledAlpaca;

    // total minted wool
    uint256 public totalMintedWool;

    /* ========== CONSTRUCTOR ========== */

    constructor(
        IWoolToken _wool,
        ICryptoAlpaca _cryptoAlpaca,
        IWoolController _woolController
    ) public {
        wool = _wool;
        woolController = _woolController;
        cryptoAlpaca = _cryptoAlpaca;
    }

    /* ========== ERC1155Receiver ========== */

    /**
     * @dev onERC1155Received implementation per IERC1155Receiver spec
     */
    function onERC1155Received(
        address,
        address _from,
        uint256 _id,
        uint256,
        bytes memory
    ) external override onlyCryptoAlpaca returns (bytes4) {
        uint256[] memory ids = _asSingletonArray(_id);
        _recycleAlpacas(_from, ids);
        return
            bytes4(
                keccak256(
                    "onERC1155Received(address,address,uint256,uint256,bytes)"
                )
            );
    }

    /**
     * @dev onERC1155BatchReceived implementation per IERC1155Receiver spec
     */
    function onERC1155BatchReceived(
        address,
        address _from,
        uint256[] memory _ids,
        uint256[] memory,
        bytes memory
    ) external override onlyCryptoAlpaca returns (bytes4) {
        _recycleAlpacas(_from, _ids);
        return
            bytes4(
                keccak256(
                    "onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"
                )
            );
    }

    /* ========== PRIVATE ========== */

    function _asSingletonArray(uint256 element)
        private
        pure
        returns (uint256[] memory)
    {
        uint256[] memory array = new uint256[](1);
        array[0] = element;

        return array;
    }

    function _recycleAlpacas(address _from, uint256[] memory _ids) private {
        uint256 woolToMint = 0;
        for (uint256 i = 0; i < _ids.length; i++) {
            // Fetch alpaca energy and state
            (, , , , , , , , , , , uint256 energy, uint256 state) = cryptoAlpaca
                .getAlpaca(_ids[i]);
            require(state == 1, "AlpacaRecycleBox: invalid alpaca state");
            require(energy > 0, "AlpacaRecycleBox: invalid alpaca energy");
            woolToMint = woolToMint.add(energy.mul(1e18));
        }

        totalRecycledAlpaca = totalRecycledAlpaca.add(_ids.length);
        totalMintedWool = totalMintedWool.add(woolToMint);

        woolController.mint(_from, woolToMint);
    }

    /* ========== MODIFIER ========== */

    modifier onlyCryptoAlpaca() {
        require(
            msg.sender == address(cryptoAlpaca),
            "AlpacaRecycleBox: received alpaca from unauthenticated contract"
        );
        _;
    }
}
