contract ERC20TransferProxy is OwnableOperatorRole {
    
    address public usdt;

    constructor(address _usdt) public {
        usdt = _usdt;
    }

    function setUSDTAddress(address _usdt) public {
        usdt = _usdt;
    }

    function erc20safeTransferFrom(address token, address from, address to, uint256 value) external onlyOperator {
        // require(token.transferFrom(from, to, value), "failure while transferring");
        if(address(token) == usdt){
            IUSDT u = IUSDT(token);
            u.transferFrom(from, to, value);
        }
        else{
            IERC20 u = IERC20(token);
            u.transferFrom(from, to, value);
        }
    }
}
