interface PlutoI {
    function mint(address _mintTo, string memory _tokenURI ) external returns (uint256);
    function getCurrentTokenId() external view returns (uint256);    
}

interface ERC20Token {
    function balanceOf(address account) external view returns (uint256);
}

contract PlutoDistributor is AccessControl {
    
    PlutoI plutoToken;

    ERC20Token cggToken;
    ERC20Token bittToken;
    
    bytes32 public constant TOGGLE_MINTING_ROLE = keccak256("TOGGLE_MINTING_ROLE");

    uint256 tokenPrice = uint256(9 * 10**16); // = 0.09 eth
    string defaultTokenURI = "https://bitverse.chainguardians.io/api/opensea/";
    address withdrawWallet;
    bool onlyMintingByTokenHoldersAllowed;

    constructor(
        PlutoI _plutoToken,
        ERC20Token _cggToken,
        ERC20Token _bittToken
    ) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(TOGGLE_MINTING_ROLE, msg.sender);

        plutoToken = _plutoToken;
        withdrawWallet = msg.sender;
        onlyMintingByTokenHoldersAllowed = true;

        cggToken = _cggToken;
        bittToken = _bittToken;

    }

    function mint(uint256 num) public payable returns (uint256) {

        require(num <= 20, "You can mint a maximum of 20");
        require(msg.value >= tokenPrice * num, "Insufficient amount provided");

        uint256 cggBalance = cggToken.balanceOf(msg.sender);
        uint256 bittBalance = bittToken.balanceOf(msg.sender);
        require(
            !onlyMintingByTokenHoldersAllowed ||
                (onlyMintingByTokenHoldersAllowed &&
                    ((cggBalance >= 100000000000000000000) ||
                        (bittBalance >= 1000000000000000000000))),
            "Insufficient CGG or BITT balance"
        );

        uint256 tokenId = plutoToken.getCurrentTokenId();
        require(tokenId + num <= 10000, "Maximum cap of 10000 mints reached");

        uint256 lastTokenMinted = 0;
        for(uint256 i; i < num; i++){
             lastTokenMinted = plutoToken.mint(
                msg.sender,
                string(abi.encodePacked(defaultTokenURI, uint2str(tokenId + i +1)))
            );
        }

        return lastTokenMinted;

    }

    fallback() external payable {}
    receive() external payable {}


    // admin functions
    function withdrawAll() public {
        uint256 _each = address(this).balance;
        require(payable(withdrawWallet).send(_each));
    }

    function updateWithdrawWallet(address _newWallet) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not admin");

        withdrawWallet = _newWallet;
    }

    function updateDefaultTokenUri(string memory _newDefaultTokenUri) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not admin");

        defaultTokenURI = _newDefaultTokenUri;
    }

    function updatePrice(uint256 _newPrice) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not admin");

        tokenPrice = _newPrice;
    }

    function toggleOnlyMintingByTokenHolders(bool _isRestricted) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender) || hasRole(TOGGLE_MINTING_ROLE, msg.sender), "Caller is not toggler or admin");

        onlyMintingByTokenHoldersAllowed = _isRestricted;
    }



    //helpers
    function uint2str(uint256 _i) internal pure returns (string memory str) {
        if (_i == 0) {
            return "0";
        }
        uint256 j = _i;
        uint256 length;
        while (j != 0) {
            length++;
            j /= 10;
        }
        bytes memory bstr = new bytes(length);
        uint256 k = length;
        j = _i;
        while (j != 0) {
            bstr[--k] = bytes1(uint8(48 + (j % 10)));
            j /= 10;
        }
        str = string(bstr);
    }

    function getSettings()
        public
        view
        returns (
            string memory _defaultTokenUri,
            uint256 _price,
            address _withdrawWallet
        )
    {
        return (defaultTokenURI, tokenPrice, withdrawWallet);
    }

}
