// File: contracts/Staker.sol

pragma solidity 0.6.12;

/*
    ###### Purpose of contract ########
    This smart contract allows users to securely stake various tokens 
    representing their share in Invictus funds (c10 etc).

    Staking tokens for certain periods of time will entitle users
    to rewards (ICT tokens).

    ICT tokens to be bought off the open-market by Invictus with fund fees,
    to create value for token holders.
*/

/** @title Staker Contract (name is WIP) */
contract Staker is Initializable {
    using SafeMath for uint256;

    ///////// Constants ///////////
    uint256 public constant DAYS_IN_SECONDS_30 = 2592000;
    uint256 public constant DAYS_IN_SECONDS_90 = 7776000;
    uint256 public constant DAYS_IN_SECONDS_180 = 15552000;
    uint256 public constant DAYS_IN_SECONDS_365 = 31536000;

    uint256 public constant WITHDRAW_PENALTY_DENOMINATOR = 10000;

    ///////// Global ///////////
    address public admin;
    address public treasury;
    mapping(uint256 => uint256) public baseWithdrawPenalty;
    mapping(address => uint256) public fundMultiplier;
    mapping(uint256 => uint256) public stakeLengthMultiplier;

    ///////// User Specific ///////////
    mapping(address => mapping(address => uint256)) public userAmountStaked; // invictus token type -> user -> amount staked
    // Lower and upper limits? Should it be an enum etc?
    mapping(address => mapping(address => uint256))
        public userCommittedStakingPeriod; // invictus token type -> user -> Time Staked
    mapping(address => mapping(address => uint256))
        public userEligibleWithdrawlTime; // invictus token -> user -> avaiable withdrawl time
    mapping(address => mapping(address => bool)) public userStakeRenew; // auto renew or withdrawing

    ///////// Invictus Contracts ///////////
    mapping(address => bool) public invictusFunds; // checking existence
    IWhitelistedRole public invictusWhitelist;

    ///////// Events ///////////
    event FundAdded(address indexed fund, uint256 multiplier);
    event StakeAdded(
        address indexed fund,
        address indexed user,
        uint256 amount,
        uint256 stakeLength,
        uint256 eligibleWithdrawTime,
        bool autoRenew
    );
    event StakeRenewed(
        address indexed fund,
        address indexed user,
        uint256 stakeLength,
        uint256 eligibleWithdrawTime,
        bool autoRenew
    );
    event StakeRemoved(address indexed fund, address indexed user);
    event StakeRemovedEarly(
        address indexed fund,
        address indexed user,
        uint256 amount
    );
    event PenaltyFeeSentToTreasury(
        address indexed fund,
        address indexed user,
        uint256 amount
    );
    event BaseWithdrawPenaltyChanged(uint256 timePeriod, uint256 amount);
    event AutoStakeUpdate(
        address indexed fund,
        address indexed user,
        bool autoRenew
    );
    event WithdrawlTimeUpdated(
        address indexed fund,
        address indexed user,
        uint256 eligibleWithdrawTime
    );
    event StakeLengthMultiplierUpdate(uint256 stakeLength, uint256 multiplier);
    event FundMultiplierUpdate(address indexed fundAddress, uint256 multiplier);
    event StakingLaunchTime(uint256 launchTimestamp);
    event DeployV0();
    event TreasuryChanged(address indexed contractAddress);

    ///////// Modifiers ///////////
    modifier onlyAdmin() {
        require(msg.sender == admin, "not admin");
        _;
    }

    modifier invictusWhitelisted() {
        require(invictusWhitelist.isWhitelisted(msg.sender), "not whitelisted");
        _;
    }

    modifier fundsWithdrawable(address fundAddress) {
        require(
            userAmountStaked[fundAddress][msg.sender] > 0,
            "nothing to withdraw"
        );
        require(
            userEligibleWithdrawlTime[fundAddress][msg.sender] <
                block.timestamp,
            "Staking time not yet fufilled"
        );
        require(
            !userStakeRenew[fundAddress][msg.sender],
            "Autorenew cannot be on"
        );
        _;
    }

    modifier fundsEarlyWithdrawable(address fundAddress) {
        require(
            userAmountStaked[fundAddress][msg.sender] > 0,
            "nothing to withdraw"
        );
        require(
            userStakeRenew[fundAddress][msg.sender] ||
                userEligibleWithdrawlTime[fundAddress][msg.sender] >=
                block.timestamp,
            "Can only early withdraw when normal withdraw not possible"
        );
        _;
    }

    modifier timePeriodCheck(uint256 timePeriod) {
        require(
            timePeriod == DAYS_IN_SECONDS_30 ||
                timePeriod == DAYS_IN_SECONDS_90 ||
                timePeriod == DAYS_IN_SECONDS_180 ||
                timePeriod == DAYS_IN_SECONDS_365,
            "Only 30, 90, 180 and 365 days valid"
        );
        _;
    }

    modifier fundExists(address fundAddress) {
        require(invictusFunds[fundAddress], "Fund does not exist");
        _;
    }

    modifier validMultiplier(uint256 multiplier) {
        require(multiplier > 0, "Multiplier should be non zero");
        require(multiplier <= 1000, "Multiplier should be less than 10x");
        _;
    }

    function initialize(
        address[] memory initialFundTokens,
        uint256[] memory initialFundTokenMultipiers,
        address _admin,
        address _treasury,
        address _invictusWhitelist,
        uint256 stakingLaunchTime
    ) public initializer {
        admin = _admin;
        treasury = _treasury;
        invictusWhitelist = IWhitelistedRole(_invictusWhitelist);

        require(initialFundTokens.length == initialFundTokenMultipiers.length);
        for (uint256 i = 0; i < initialFundTokens.length; i++) {
            address intialFundToken = initialFundTokens[i];
            uint256 intialFundTokenMultiplier = initialFundTokenMultipiers[i];
            invictusFunds[intialFundToken] = true;
            fundMultiplier[intialFundToken] = intialFundTokenMultiplier;
            emit FundAdded(intialFundToken, intialFundTokenMultiplier);
        }

        baseWithdrawPenalty[DAYS_IN_SECONDS_30] = 25;
        baseWithdrawPenalty[DAYS_IN_SECONDS_90] = 50;
        baseWithdrawPenalty[DAYS_IN_SECONDS_180] = 100;
        baseWithdrawPenalty[DAYS_IN_SECONDS_365] = 200;

        stakeLengthMultiplier[DAYS_IN_SECONDS_30] = 100;
        stakeLengthMultiplier[DAYS_IN_SECONDS_90] = 133;
        stakeLengthMultiplier[DAYS_IN_SECONDS_180] = 166;
        stakeLengthMultiplier[DAYS_IN_SECONDS_365] = 200;

        emit BaseWithdrawPenaltyChanged(DAYS_IN_SECONDS_30, 25);
        emit BaseWithdrawPenaltyChanged(DAYS_IN_SECONDS_90, 50);
        emit BaseWithdrawPenaltyChanged(DAYS_IN_SECONDS_180, 100);
        emit BaseWithdrawPenaltyChanged(DAYS_IN_SECONDS_365, 200);

        emit StakeLengthMultiplierUpdate(DAYS_IN_SECONDS_30, 100);
        emit StakeLengthMultiplierUpdate(DAYS_IN_SECONDS_90, 133);
        emit StakeLengthMultiplierUpdate(DAYS_IN_SECONDS_180, 166);
        emit StakeLengthMultiplierUpdate(DAYS_IN_SECONDS_365, 200);

        if (stakingLaunchTime < block.timestamp) {
            emit StakingLaunchTime(block.timestamp);
        } else {
            emit StakingLaunchTime(stakingLaunchTime);
        }
        emit DeployV0();
        emit TreasuryChanged(_treasury);
    }

    function changeAdmin(address _admin) external onlyAdmin {
        admin = _admin;
    }

    function addNewInvictusFund(address fundAddress, uint256 _fundMultiplier)
        external
        onlyAdmin
        validMultiplier(_fundMultiplier)
    {
        invictusFunds[fundAddress] = true;
        fundMultiplier[fundAddress] = _fundMultiplier;
        emit FundAdded(fundAddress, _fundMultiplier);
    }

    function changeTreasury(address _treasury) external onlyAdmin {
        treasury = _treasury;
        emit TreasuryChanged(_treasury);
    }

    function changeBasePenalty(uint256 timePeriod, uint256 penaltyRate)
        external
        onlyAdmin
        timePeriodCheck(timePeriod)
    {
        // max penalty 50% for saftey
        require(penaltyRate <= 5000, "penalty exceeds 50%");

        baseWithdrawPenalty[timePeriod] = penaltyRate;
        emit BaseWithdrawPenaltyChanged(timePeriod, penaltyRate);
    }

    function changeStakeLengthMultiplier(
        uint256 stakeLength,
        uint256 multiplier
    )
        external
        onlyAdmin
        timePeriodCheck(stakeLength)
        validMultiplier(multiplier)
    {
        stakeLengthMultiplier[stakeLength] = multiplier;
        emit StakeLengthMultiplierUpdate(stakeLength, multiplier);
    }

    function changeFundMultiplier(address fundAddress, uint256 multiplier)
        external
        onlyAdmin
        fundExists(fundAddress)
        validMultiplier(multiplier)
    {
        fundMultiplier[fundAddress] = multiplier;
        emit FundMultiplierUpdate(fundAddress, multiplier);
    }

    function _eligibleWithdrawTimeUpdateHelper(
        uint256 blockTimestampOldEligibleWithdrawTimeDelta,
        uint256 stakingPeriod
    ) internal pure returns (uint256) {
        return
            blockTimestampOldEligibleWithdrawTimeDelta -
            (blockTimestampOldEligibleWithdrawTimeDelta % stakingPeriod) +
            stakingPeriod;
    }

    // If auto renew is on the staking period needs to be reset
    // I.e. a user stakes for 1 month with autorenew.
    // after 7.5 months, they should next be eligible to withdraw at month 8 etc.
    function _autoStakingUpdateEligibleWithdrawlTime(address fundAddress)
        internal
    {
        if (userStakeRenew[fundAddress][msg.sender]) {

                uint256 oldWithdrawlTime
             = userEligibleWithdrawlTime[fundAddress][msg.sender];
            uint256 stakingPeriod = userCommittedStakingPeriod[fundAddress][msg
                .sender];

            if (oldWithdrawlTime < block.timestamp) {
                uint256 timeToAdd = _eligibleWithdrawTimeUpdateHelper(
                    block.timestamp - oldWithdrawlTime,
                    stakingPeriod
                );

                userEligibleWithdrawlTime[fundAddress][msg
                    .sender] = oldWithdrawlTime.add(timeToAdd);

                emit WithdrawlTimeUpdated(
                    fundAddress,
                    msg.sender,
                    userEligibleWithdrawlTime[fundAddress][msg.sender]
                );
            }
        }
    }

    // User can only toggle auto renew if they have a live stake not currently withdrawable
    // If there stake has lapsed, they will need to call stake renew or stake remove.
    function setAutoRenewStakeStatus(address fundAddress, bool autoRenewStake)
        external
        fundsEarlyWithdrawable(fundAddress)
    {
        require(
            autoRenewStake != userStakeRenew[fundAddress][msg.sender],
            "Can not change autorenew to what its already set to"
        );
        _autoStakingUpdateEligibleWithdrawlTime(fundAddress);
        userStakeRenew[fundAddress][msg.sender] = autoRenewStake;
        emit AutoStakeUpdate(fundAddress, msg.sender, autoRenewStake);
    }

    /*
    Auto staking function. 
    */
    function stake(
        address fundAddress,
        uint256 amount,
        uint256 stakeLength,
        bool autoRenewStake
    )
        public
        invictusWhitelisted
        timePeriodCheck(stakeLength)
        fundExists(fundAddress)
    {
        // This ensure if user is adding more funds to stake, they cannot attack and decrease the
        // staking period to say 1 seconds for early withdrawl. They can increase however.
        // edge case, topping up after their stake expired, they should be able to use a smaller stake length
        require(
            stakeLength >= userCommittedStakingPeriod[fundAddress][msg.sender],
            "cannot decrease staking length"
        );

        ERC20PresetMinterPauserUpgradeSafe(fundAddress).transferFrom(
            msg.sender,
            address(this),
            amount
        );

        userAmountStaked[fundAddress][msg
            .sender] = userAmountStaked[fundAddress][msg.sender].add(amount);
        userCommittedStakingPeriod[fundAddress][msg.sender] = stakeLength;
        userEligibleWithdrawlTime[fundAddress][msg.sender] = block
            .timestamp
            .add(stakeLength);
        userStakeRenew[fundAddress][msg.sender] = autoRenewStake;

        emit StakeAdded(
            fundAddress,
            msg.sender,
            amount,
            stakeLength,
            userEligibleWithdrawlTime[fundAddress][msg.sender],
            autoRenewStake
        );
    }

    /*
    Allows staking tokens from multi funds in single call. 
    */
    function multiStake(
        address[] memory fundAddress,
        uint256[] memory amount,
        uint256[] memory stakeLength,
        bool[] memory autoRenewStake
    ) external {
        require(
            fundAddress.length == amount.length,
            "bad input length: amount"
        );
        require(
            fundAddress.length == stakeLength.length,
            "bad input length: stakeLength"
        );
        require(
            fundAddress.length == autoRenewStake.length,
            "bad input length: autoRenew"
        );

        for (uint256 i = 0; i < fundAddress.length; i++) {
            stake(fundAddress[i], amount[i], stakeLength[i], autoRenewStake[i]);
        }
    }

    /*
    You didn't put auto staking on but you'd actually like to restake
    your funds that are already in the contract.
    */
    function restake(
        address fundAddress,
        uint256 stakeLength,
        bool autoRenewStake
    ) external timePeriodCheck(stakeLength) fundsWithdrawable(fundAddress) {
        userCommittedStakingPeriod[fundAddress][msg.sender] = stakeLength;
        userEligibleWithdrawlTime[fundAddress][msg.sender] = block
            .timestamp
            .add(stakeLength);

        userStakeRenew[fundAddress][msg.sender] = autoRenewStake;

        emit StakeRenewed(
            fundAddress,
            msg.sender,
            stakeLength,
            userEligibleWithdrawlTime[fundAddress][msg.sender],
            autoRenewStake
        );
    }

    /*
    Checks the user has served the required staking period.
    Also check the user is not autorenewing. If they are,
    The elgibile withdrawl is set to when they should next 
    be allowed to withdraw based on the auto renew.
    */
    function withdraw(address fundAddress)
        public
        fundsWithdrawable(fundAddress)
    {
        uint256 amount = userAmountStaked[fundAddress][msg.sender];
        userAmountStaked[fundAddress][msg.sender] = 0;
        //reset this. Case user stakes at 6months. later withdraws.
        // Should be able to stake again with 3months but top-up prevents if not reset
        userCommittedStakingPeriod[fundAddress][msg.sender] = 0;
        ERC20PresetMinterPauserUpgradeSafe(fundAddress).transfer(
            msg.sender,
            amount
        );
        emit StakeRemoved(fundAddress, msg.sender);
    }

    function multiWithdraw(address[] memory fundAddresses) external {
        for (uint256 i = 0; i < fundAddresses.length; i++) {
            withdraw(fundAddresses[i]);
        }
    }

    function _basePenaltyFunction(uint256 stakeLength)
        internal
        view
        returns (uint256)
    {
        if (stakeLength == DAYS_IN_SECONDS_30) {
            return baseWithdrawPenalty[DAYS_IN_SECONDS_30];
        } else if (stakeLength <= DAYS_IN_SECONDS_90) {
            return baseWithdrawPenalty[DAYS_IN_SECONDS_90];
        } else if (stakeLength <= DAYS_IN_SECONDS_180) {
            return baseWithdrawPenalty[DAYS_IN_SECONDS_180];
        } else {
            return baseWithdrawPenalty[DAYS_IN_SECONDS_365];
        }
    }

    function earlyWithdraw(address fundAddress)
        external
        fundsEarlyWithdrawable(fundAddress)
    {
        uint256 amount = userAmountStaked[fundAddress][msg.sender];
        userAmountStaked[fundAddress][msg.sender] = 0;

        _autoStakingUpdateEligibleWithdrawlTime(fundAddress);
        uint256 _stakeLength = userCommittedStakingPeriod[fundAddress][msg
            .sender];
        userCommittedStakingPeriod[fundAddress][msg.sender] = 0;

        uint256 basePenaltyRate = _basePenaltyFunction(_stakeLength);
        uint256 penaltyFee = amount
            .mul(basePenaltyRate)
            .mul(
            userEligibleWithdrawlTime[fundAddress][msg.sender].sub(
                block.timestamp
            )
        )
            .div(_stakeLength)
            .div(WITHDRAW_PENALTY_DENOMINATOR);

        uint256 amountToRedeem = amount.sub(penaltyFee);

        // Transfer to treasury
        ERC20PresetMinterPauserUpgradeSafe(fundAddress).transfer(
            treasury,
            penaltyFee
        );

        // Transfer to user
        ERC20PresetMinterPauserUpgradeSafe(fundAddress).transfer(
            msg.sender,
            amountToRedeem
        );

        emit StakeRemovedEarly(fundAddress, msg.sender, amountToRedeem);
        emit PenaltyFeeSentToTreasury(fundAddress, msg.sender, penaltyFee);
    }
}
