contract HASOWNER{

address private owner;
bool public isPaused;

constructor(){
    owner=msg.sender;
}

function pause()public onlyOwner{
    isPaused =true;
}
function unPause()public onlyOwner{
    isPaused=false;
}

function changeOwner(address newowner) public onlyOwner{
    owner=newowner;
}

modifier onlyOwner(){
    require(msg.sender==owner,'only owner can call this');
    _;
}

}
contract EEGGSCOIN is ERC20 ,HASOWNER{

    uint256 private constant INITIAL_SUPPLY = 7771111444 * 10**18;  
    uint256 private _tTotal;
    address private development = 0xB84Bb79Ab8e4920c69B8c47bdb6BCF0d33C344b3;
    address private teamfoundersadvisors = 0x73EDF3b3a7572d39051Ba9300220c27a40AdD453;
    address private reserve = 0xF42b8f0Eac6d77e600B1f387EbB6872A068c2152;
    address private liquidity = 0xEF6E12de1C58B19a69A587cf7a71e705611b8cCB;
    address payable private ownerof ;//= 0x88F9861Abdf01C960928486ed66F4Ffd3F955a2E;
    
    //PRESALE
    uint256 public minAmountofEth = 0.0000014 * 10**18;   // can updated
    uint256 public maxAmountofEth = 1000 * 10**18; 
    bool public presaleEnabled = true; // change to false after testing
    address public wallet;
    uint256 public presalePrice =0.00000149537 * 10**18; // 0.0000149537 * 10**18; // 0.0000014953700000000
    uint256 public weiRaised;
    uint32 public StartDayInEpochTime = 1638736549;
    address[]  public investors;
    uint private presaleTokensSold;

    constructor() ERC20("EEGGS", "EEGGS") {
        _tTotal = INITIAL_SUPPLY;
        uint devTokens= calculatePercentage(_tTotal, 20);
        uint256 teamTokens = calculatePercentage(_tTotal, 20);
        uint256 reserveTokens = calculatePercentage(_tTotal, 5);
        uint256 liquidityTokens = calculatePercentage(_tTotal, 4);
        uint256 ownerTokens = calculatePercentage(_tTotal, 41);  
        uint presaleAmount = calculatePercentage(_tTotal, 10);
       
        // rember to remove the next line
        ownerof=payable(msg.sender);

        _mint( address(development), devTokens);
        _mint( address(teamfoundersadvisors), teamTokens);
        _mint( address(reserve), reserveTokens);
        _mint( address(liquidity), liquidityTokens);  
        _mint(ownerof,ownerTokens); 
        _mint(address(this),presaleAmount);
    }
    function tokensLeftInPresale()public{
        balanceOf(address(this));
    }
    function changePresalePrice(uint price)public{
        presalePrice= price* 10**18;
    }
    function closePresale()public{
        presaleEnabled=false;
    }
    function burn(uint amount)public{
        _burn(msg.sender,amount);
    }
    
    function calculatePercentage(uint amount, uint percentage)private returns(uint){
        return amount * percentage / 100;
    }
  
    function roundIt(uint number) public returns(uint){
        return number - number  % 1;
    }

    function withdrawAll() external onlyOwner {
        address payable myContract =  payable(address(this));
        address payable theOwner= payable(msg.sender);
        theOwner.transfer(myContract.balance);

    }
    function distributeEEGGS(address _beneficiary) public payable  {   
        require(_beneficiary != address(0));
        require(msg.value >= minAmountofEth,'NOT ENOUGH ETH DEPOSITED');
        require(msg.value <= maxAmountofEth,'MAX DEPOSIT AMOUNT EXCEEDED');
        require(msg.value != 0);
        
        uint256 amountofEEGGStobuy = msg.value/presalePrice;
        uint stateAfterSale=presaleTokensSold+ presalePrice;
        require(stateAfterSale >= presalePrice,"not enough tokens"); 
        _mint(msg.sender,amountofEEGGStobuy);
        _burn(address(this),amountofEEGGStobuy);
        presaleTokensSold= presaleTokensSold + amountofEEGGStobuy;
        weiRaised = weiRaised + msg.value;
    }
    function getInvestorCount() public view returns(uint count) {
    return investors.length;
}    

function getInvestor(uint count) public view  returns(address){
    return investors[count];
}

    fallback() payable external {
        if(presaleEnabled==true){
            distributeEEGGS(msg.sender);
        }else{
            revert();
        }
    }

}
