pragma solidity ^0.8.10;






contract Babyccinos is ERC721, ERC721Enumerable, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;
    using Address for address;

    // Sale Information
    uint256 public constant MAX_CINOS = 10000;
    uint256 public constant MAX_PER_MINT = 5;
    uint256 public constant MAX_CINOS_MINT = 50;
    uint256 public cosmiccoffeeHolderprice = 0; // 0.00 ETH (just pay gas fees)
    uint256 public preSaleprice = 50000000000000000 ; // 0.05 ETH
    uint256 public publicSaleprice = 70000000000000000 ; // 0.07 ETH

    // Token Availability
    uint256 public reservedClaimed;
    uint256 public numCinosMinted;
    uint256 public reserved = 1000; // Reserved for the team, giveaways, collabs and so on

    // The base link that leads to the metadata and image for the token
    string public baseTokenURI;

    //Starting and stopping the sale
    bool public holderClaimStarted = false;
    bool public presaleStarted = false;
    bool public publicSaleStarted = false;


    // Team addresses for withdrawals
    address public a1 = 0xcc8d8F2451Dc756FB612DDD104ee148EdFCDD146; //Artist Wallet
    address public a2 = 0x8AA41A73deAD00D9DE913E4B10273D050703Fe3d; //Dev Wallet
    address public a3 = 0xdF968Caf4C2fB59bDb2Bfd9f43d06fa4491b38fb; //Founder Wallet
    address public a4 = 0xb5C224e0c8E5D7587b49434F858332AB95A79434; //Community Wallet
    address public a5 = 0x8dA2666D10f887a6D71EbD8577e6df80F6E2aB4e; //Charity Wallet

    //mapping 
    mapping(address => bool) private _holderEligible;
    mapping(address => bool) private _presaleEligible;
    mapping(address => uint256) private _totalClaimed;


    // events
    event BaseURIChanged(string baseURI);
    event HolderMint(address minter, uint256 amountOfCinos);
    event PresaleMint(address minter, uint256 amountOfCinos);
    event PublicSaleMint(address minter, uint256 amountOfCinos);

    //modifiers
    modifier whenHolderClaimStarted() {
        require(holderClaimStarted, "Cosmic Coffee holder claim of Babyccinos is not active fren");
        _;
    }

    modifier whenPresaleStarted() {
        require(presaleStarted, "Presale of Babyccinos is not active fren");
        _;
    }

    modifier whenPublicSaleStarted() {
        require(publicSaleStarted, "Public sale of Babyccinos is not active ser");
        _;
    }

    constructor (string memory newBaseURI) ERC721 ("Babyccinos", "CINOS") {
        setBaseURI(newBaseURI);

        numCinosMinted += 1;
        _safeMint(msg.sender, numCinosMinted);
    }
    
    ///// Administrative Sales Prep  /////

    // Admin minting to use for giveaways, collabs, and so on...
    function claimReserved(address recipient, uint256 amount) external onlyOwner {
        require(reservedClaimed != reserved, "All reserved Babyccinos have been claimed");
        require(reservedClaimed + amount <= reserved, "Minting would exceed max reserved Babyccinos");
        require(recipient != address(0), "Cannot add null address");
        require(totalSupply() < MAX_CINOS, "All tokens have been minted");
        require(totalSupply() + amount <= MAX_CINOS, "Minting would exceed max supply");

        uint256 _nextTokenId = numCinosMinted + 1;

        for (uint256 i = 0; i < amount; i++) {
            _safeMint(recipient, _nextTokenId + i);
        }
        numCinosMinted += amount;
        reservedClaimed += amount;
    }

    // add addresses to Cosmic Coffee Holder claim
    function addToHolderClaim(address[] calldata addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            require(addresses[i] != address(0), "Cannot add null address");

            _holderEligible[addresses[i]] = true;

            _totalClaimed[addresses[i]] > 0 ? _totalClaimed[addresses[i]] : 0;
        }
    }

    // add addresses to Babyccino PreSale
    function addToPresale(address[] calldata addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            require(addresses[i] != address(0), "Cannot add null address");

            _presaleEligible[addresses[i]] = true;

            _totalClaimed[addresses[i]] > 0 ? _totalClaimed[addresses[i]] : 0;    }
    }

    ///// Babyccino Minting options /////
    // Cosmic Coffee holder Minting of Babyccinos limit of 5
    function mintHolder(uint256 amountOfCinos) external payable whenHolderClaimStarted {
        require(_holderEligible[msg.sender], "You are not eligible for the Cosmic Coffee holder minting");
        require(totalSupply() < MAX_CINOS, "All tokens have been minted");
        require(amountOfCinos <= MAX_PER_MINT, "Cannot purchase this many tokens during presale");
        require(totalSupply() + amountOfCinos <= MAX_CINOS, "Minting would exceed max supply");
        require(_totalClaimed[msg.sender] + amountOfCinos <= MAX_PER_MINT, "Purchase exceeds max allowed");
        require(amountOfCinos > 0, "Must mint at least one babyccino");
        require(cosmiccoffeeHolderprice * amountOfCinos == msg.value, "ETH amount is incorrect");

        for (uint256 i = 0; i < amountOfCinos; i++) {
            uint256 tokenId = numCinosMinted + 1;

            numCinosMinted += 1;
            _totalClaimed[msg.sender] += 1;
            _safeMint(msg.sender, tokenId);
        }

        emit HolderMint(msg.sender, amountOfCinos);
    }

    // Pre-Sale Minting of Babyccinos
    function mintPresale(uint256 amountOfCinos) external payable whenPresaleStarted {
        require(_presaleEligible[msg.sender], "You are not eligible for the presale");
        require(totalSupply() < MAX_CINOS, "All tokens have been minted");
        require(amountOfCinos <= MAX_PER_MINT, "Cannot purchase this many tokens in a transaction");
        require(totalSupply() + amountOfCinos <= MAX_CINOS, "Minting would exceed max supply");
        require(_totalClaimed[msg.sender] + amountOfCinos <= MAX_CINOS_MINT, "Purchase exceeds max allowed");
        require(amountOfCinos > 0, "Must mint at least one babyccino");
        require(preSaleprice * amountOfCinos == msg.value, "ETH amount is incorrect");

        for (uint256 i = 0; i < amountOfCinos; i++) {
            uint256 tokenId = numCinosMinted + 1;

            numCinosMinted += 1;
            _totalClaimed[msg.sender] += 1;
            _safeMint(msg.sender, tokenId);
        }

        emit PresaleMint(msg.sender, amountOfCinos);
    }

    // Public Minting of Babyccions
    function mint(uint256 amountOfCinos) external payable whenPublicSaleStarted {
        require(totalSupply() < MAX_CINOS, "All tokens have been minted");
        require(amountOfCinos <= MAX_PER_MINT, "Cannot purchase this many tokens in a transaction");
        require(totalSupply() + amountOfCinos <= MAX_CINOS, "Minting would exceed max supply");
        require(_totalClaimed[msg.sender] + amountOfCinos <= MAX_CINOS_MINT, "Purchase exceeds max allowed per address");
        require(amountOfCinos > 0, "Must mint at least one babyccino");
        require(publicSaleprice * amountOfCinos == msg.value, "ETH amount is incorrect");

        for (uint256 i = 0; i < amountOfCinos; i++) {
            uint256 tokenId = numCinosMinted + 1;

            numCinosMinted += 1;
            _totalClaimed[msg.sender] += 1;
            _safeMint(msg.sender, tokenId);
        }

        emit PublicSaleMint(msg.sender, amountOfCinos);
    }

    ///// Check Eligibibly and Token Owner /////
    // enter address to determine if eligible for Cosmic Coffee holder claim
    function checkHolderEligiblity(address addr) external view returns (bool) {
        return _presaleEligible[addr];
    }

    // enter address to determine if eligible for the pre-sale
    function checkPresaleEligiblity(address addr) external view returns (bool) {
        return _presaleEligible[addr];
    }

    // check to see how many Babyccinos have been claimed
    function amountClaimedBy(address owner) external view returns (uint256) {
        require(owner != address(0), "Cannot add null address");

        return _totalClaimed[owner];
    }

    // See which address owns which tokens
    function tokensOfOwner(address addr) public view returns(uint256[] memory) {
        uint256 tokenCount = balanceOf(addr);
        uint256[] memory tokensId = new uint256[](tokenCount);
        for(uint256 i; i < tokenCount; i++){
            tokensId[i] = tokenOfOwnerByIndex(addr, i);
        }
        return tokensId;
    }

    ///// Owner Administrative Functions /////
    // Toggle to start/stop Cosmic Coffee Holder Sale
    function toggleHolderClaimStarted() external onlyOwner {
        holderClaimStarted = !holderClaimStarted;
    }

    // Toggle to start/stop Pre-Sale
    function togglePresaleStarted() external onlyOwner {
        presaleStarted = !presaleStarted;
    }

    // Toggle to start/stop Public Sale
    function togglePublicSaleStarted() external onlyOwner {
        publicSaleStarted = !publicSaleStarted;
    }

    // Override so the openzeppelin tokenURI() method will use this method to create the full tokenURI instead
    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }

    // Update Base URI post deployment of smart contract
    function setBaseURI(string memory baseURI) public onlyOwner {
        baseTokenURI = baseURI;
        emit BaseURIChanged(baseURI);
    }

    // Set a different public mint price in case ETH changes drastically
    function setPrice(uint256 newPrice) public onlyOwner {
        publicSaleprice = newPrice;
    }

    // Set team addresses
    function setAddresses(address[] memory _a) public onlyOwner {
        a1 = _a[0];
        a2 = _a[1];
        a3 = _a[2];
        a4 = _a[3];
        a5 = _a[4];
    }

    // Withdraw funds from contract for the team
    function withdrawTeam(uint256 amount) public payable onlyOwner {
        uint256 percent = amount / 100;
        // 20% to each wallet
        require(payable(a1).send(percent * 20)); // Artist Wallet
        require(payable(a2).send(percent * 20)); // Dev Wallet
        require(payable(a3).send(percent * 10)); // Founder Wallet
        require(payable(a4).send(percent * 40)); // Community Wallet
        require(payable(a5).send(percent * 15)); // Charity Wallet: Half will go to organizations supporting children's reading, music, and art programs; underserved communities, and other organizations identified by the community, while the other half will invest back into community (and neighboring community) projects
    }

    // The following functions are overrides required by Solidity.
    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override (ERC721,ERC721Enumerable) returns (bool) 
    {
        return super.supportsInterface(interfaceId);
    }
}
