contract KnightStaking is IERC721Receiver, Ownable {
  using SafeMath for uint256;
  bytes4 private constant _ERC721_RECEIVED = 0x150b7a02;

  event RewardAdded(uint256 time, uint256 rewardAmount);
  event RewardClaimed(uint256 tokenID, address owner);

  event Staked(address staker, uint256 tokenID, uint256 stakedTime);
  event UnStaked(uint256 tokenID, uint256 unstakedTime);

  /*
  Used to store the information of the Staked NFT.
  */
  struct StakedNFT{
    uint256 startTime;
    uint256 claimableReward;
    address staker;
    bool isStaked;
    uint256 lastClaims;
  }

  //staked tokenID list 
  uint256[] public stakedNFTs;
  //maps tokenID => Staked NFT
  mapping(uint256 => StakedNFT) public stakedInfo;
  //nft address
  address public nft;

  constructor(address _nft){
    nft = _nft;
  }

  /*
  Handle the eth receive function 
  */
  receive() external payable{
    addReward();
  }

  // reward = ( YourNFTStakedTime / TotalNFTStakedTime ) * AmountDistributed 
  function addReward() public payable{
    if(msg.value>0){
        //perform distribution
        uint256 totalTime = 0;
        for(uint256 i=0; i < stakedNFTs.length ; i++){
            StakedNFT memory staked = stakedInfo[stakedNFTs[i]];
            totalTime += block.timestamp.sub(staked.startTime);
        }
        for(uint256 i=0; i< stakedNFTs.length; i++){
            StakedNFT storage _stakedInfo = stakedInfo[stakedNFTs[i]];
            uint256 timeSpan = block.timestamp.sub(_stakedInfo.startTime);
            uint256 reward = timeSpan.mul(msg.value).div(totalTime);
            _stakedInfo.startTime = block.timestamp;
            _stakedInfo.claimableReward = reward; 
        }
        emit RewardAdded(block.timestamp, msg.value);
    }
   
  }

  function batchStakeNFT(uint256[] memory tokenIDs) external{
      uint256 tokenID = 0;
      for(uint256 i=0; i<tokenIDs.length; i++){
         tokenID = tokenIDs[i];
         require(IERC721(nft).ownerOf(tokenID)==msg.sender, "Staking: Not the NFT owner!");
         require(IERC721(nft).isApprovedForAll(msg.sender, address(this)), "Staking:Staking contract not set as operator!");
        IERC721(nft).safeTransferFrom(msg.sender, address(this), tokenID);
        _stakeNFT(tokenID);
      }
  }

  function stakeNFT(uint256 tokenID) public {
    require(IERC721(nft).ownerOf(tokenID)==msg.sender, "Staking: Not the NFT owner!");
    require(
        address(this) == IERC721(nft).getApproved(tokenID) || IERC721(nft).isApprovedForAll(msg.sender, address(this)), 
        "Staking: NFT not approved for the staking address OR Staking contract not set as operator!"
    );
    IERC721(nft).safeTransferFrom(msg.sender, address(this), tokenID);
    _stakeNFT(tokenID);
  }

  function _stakeNFT(uint256 _tokenID) internal{
    stakedNFTs.push(_tokenID);

    if(hasNFTData(_tokenID)){
      StakedNFT storage stakedNFT = stakedInfo[_tokenID];
      require(!stakedNFT.isStaked, "Staking: NFT already staked!");
      stakedNFT.staker = msg.sender;
      stakedNFT.startTime = block.timestamp;
      stakedNFT.isStaked = true;
    }
    else{
      StakedNFT memory stakedNFT = StakedNFT({
        startTime: block.timestamp,
        claimableReward: 0,
        staker: msg.sender,
        isStaked: true,
        lastClaims: 0
      });
      stakedInfo[_tokenID] = stakedNFT;
    }
    emit Staked(msg.sender, _tokenID, block.timestamp);
  }

  function batchUnStakeNFT(uint256[] memory tokenIDs) external{
      uint256 tokenID = 0;
      for(uint256 i=0; i<tokenIDs.length; i++){
         tokenID = tokenIDs[i];
         claimReward(tokenID);
         IERC721(nft).safeTransferFrom(address(this), msg.sender, tokenID);
         _unstakeNFT(tokenID);
      }
  }

  function unstakeNFT(uint256 tokenID) public {
    claimReward(tokenID);
    IERC721(nft).safeTransferFrom(address(this), msg.sender, tokenID);
    _unstakeNFT(tokenID);
  }

  function _unstakeNFT(uint256 _tokenID) internal {
    StakedNFT storage stakedNFT = stakedInfo[_tokenID];
    require(stakedNFT.isStaked, "Staking: NFT not staked!");
    require(stakedNFT.staker == msg.sender, "Staking: Not the owner of NFT!");

    stakedNFT.isStaked = false;
    stakedNFT.startTime = 0;

    uint256 size = stakedNFTs.length;
    uint256 lastElement = stakedNFTs[size-1];
    stakedNFTs.pop();


    emit UnStaked(_tokenID, block.timestamp);

    for(uint256 i=0; i< size-1 ; i++){
      if(stakedNFTs[i] ==_tokenID){
        stakedNFTs[i] = lastElement;
        return;
      }
    }

  }

  function hasNFTData(uint256 _tokenID) internal view returns(bool){
    if(stakedInfo[_tokenID].staker != address(0)){
      return true;
    }
    return false;
  }

  function claimReward(uint256 tokenID) public{
    StakedNFT storage stakedNFT = stakedInfo[tokenID];
    require(stakedNFT.staker == msg.sender, "Staking: Not the NFT owner");
    uint256 claimableEth = stakedNFT.claimableReward;
    stakedNFT.claimableReward = 0;
    stakedNFT.lastClaims = block.timestamp;
    stakedNFT.startTime = block.timestamp;
    if(claimableEth>0){
      withdrawEth(claimableEth);
    }
  }

  function withdrawEth(uint256 amount) internal{
    (bool os, ) = payable(msg.sender).call{value: amount}("");
    require(os);
  }

  // getters
  function getClaimableEth(uint256 tokenID) external view returns(uint256 ethValue){
    return stakedInfo[tokenID].claimableReward;
  }

  function getStakedNFTInfo(uint256 tokenID) external view returns(StakedNFT memory stakedNft){
    stakedNft = stakedInfo[tokenID];
  }

  function getStakedTime(uint256 tokenID) external view returns(uint256 startTime){
    startTime = stakedInfo[tokenID].startTime;
  }

  function getStakedInfo(uint256 tokenID) external view returns(StakedNFT memory nftInfo){
    nftInfo = stakedInfo[tokenID];
  }

  /*
  Handle the ERC721 recieve.
  */
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata data
    )
        public override returns(bytes4)
    {
        return _ERC721_RECEIVED;
    }


  //ERC721 support 
  function supportsInterface(bytes4 interfaceID) external returns (bool){
   
    return interfaceID == _ERC721_RECEIVED ;
  }

}
