// import "hardhat/console.sol";

import "./IERC1155.sol";
import "./IERC721.sol";

contract GFT {
    constructor() public {}

    function distributeSame1155s(
        address nft,
        uint256 tokenID,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) public payable {
        require(recipients.length == amounts.length);

        for (uint256 i = 0; i < recipients.length; i++) {
            ERC1155(nft).safeTransferFrom(
                msg.sender,
                recipients[i],
                tokenID,
                amounts[i],
                ""
            );

            if (msg.value > 0)
                recipients[i].call{value: msg.value / recipients.length}("");
        }
    }

    function distribute1155s(
        address nft,
        address[] calldata recipients,
        uint256[] calldata tokenIDs,
        uint256[] calldata amounts
    ) public payable {
        require(tokenIDs.length == recipients.length);
        require(recipients.length == amounts.length);

        for (uint256 i = 0; i < recipients.length; i++) {
            ERC1155(nft).safeTransferFrom(
                msg.sender,
                recipients[i],
                tokenIDs[i],
                amounts[i],
                ""
            );

            if (msg.value > 0)
                recipients[i].call{value: msg.value / recipients.length}("");
        }
    }

    function distribute721s(
        address nft,
        address[] calldata recipients,
        uint256[] calldata tokenIDs
    ) public payable {
        require(tokenIDs.length == recipients.length);

        for (uint256 i = 0; i < recipients.length; i++) {
            ERC721(nft).safeTransferFrom(
                msg.sender,
                recipients[i],
                tokenIDs[i]
            );

            if (msg.value > 0)
                recipients[i].call{value: msg.value / recipients.length}("");
        }
    }
}

