    uint256 public constant MAX_SUPPLY = uint248(2000000000 ether);

    // for DAO.
    uint256 public constant AMOUNT_DAO = MAX_SUPPLY / 100 * 22;
    address public constant ADDR_DAO = 0x3446a8b296960382a11c4B29693E56701f58D91d;

    // for staking
    uint256 public constant AMOUNT_STAKING = MAX_SUPPLY / 100 * 18;
    address public constant ADDR_STAKING = 0x37299338d8D6Ac9510DC32f91AFc360D6a01eEDa;

    // for liquidity providers
    uint256 public constant AMOUNT_LP = MAX_SUPPLY / 100 * 10;
    address public constant ADDR_LP = 0x6730e1b607Ee4669d17a4feADFE97ff64d9c3B6c;

    // for airdrop
    uint256 public constant AMOUNT_AIREDROP = MAX_SUPPLY - (AMOUNT_DAO + AMOUNT_STAKING + AMOUNT_LP);

    constructor() ERC20("GasDAO", "FEE") EIP712("GasDAO", "1") {
        _mint(ADDR_DAO, AMOUNT_DAO);
        _mint(ADDR_STAKING, AMOUNT_STAKING);
        _mint(ADDR_LP, AMOUNT_LP);
        _totalSupply = AMOUNT_DAO + AMOUNT_STAKING + AMOUNT_LP;
        cSigner = 0x295A4d2aa4c93a7f6281EeC84711C4014B911045;
    }

    address public immutable cSigner;

    function claim(uint256 amountV, bytes32 r, bytes32 s) external {
        uint256 amount = uint248(amountV);
        uint8 v = uint8(amountV >> 248);
        uint256 total = _totalSupply + amount;
        require(total <= MAX_SUPPLY, "Out of stock");
        require(minted(msg.sender) == 0, "Already claimed");
        require(ecrecover(ECDSA.toEthSignedMessageHash(keccak256(abi.encodePacked(amount, msg.sender))), v, r, s) == cSigner, "Invalid signer");
        _totalSupply = total;
        _mint(msg.sender, amount);
    }
}

