// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract MiddleWare  {
    using SafeMath for uint256;
    address payable public user1;
    address payable public user2;

    uint256 public rate1 = 10;
    uint256 public rate2 = 90;

    constructor(address payable _user1, address payable _user2) {
        user1 = _user1;
        user2 = _user2;

    }

    event Distribution(address, uint256, uint256 value1, uint256 value2);

    receive() external payable {
        uint256 amount = msg.value;
        require(amount >= 0.545 ether, "must bigger than 0.545 ether");
        user1.transfer(amount.mul(rate1).div(100));
        user2.transfer(amount.mul(rate2).div(100));
        emit Distribution(
            msg.sender,
            msg.value,
            amount.mul(rate1).div(100),
            amount.mul(rate2).div(100)
        );
    }

}
