/**
 * wROOT MultiBridge
 *
 * Attributes:
 * - Stores cross-chain transfers until they are processed
 * - Supports the tracking of cross-chain transfers via hash-based IDs
 * - Ensures users fund the necessary gas cost for the migrations based on a flag
 */
abstract contract MultiBridge is Controller {
    using SafeERC20 for ERC20;
    using SafeMath for uint256;
    using Address for address payable;

    mapping (uint256 => bool) public isSupportedChain;

    struct CrossChainTransfer {
        address to;
        bool processed;
        uint88 gasPrice;
        uint256 amount;
        uint256 chain;
    }

    // Numeric flag for processed inward transfers
    uint256 internal constant PROCESSED = type(uint256).max;
    // Gas Costs
    // - Process gas cost
    uint256 private constant PROCESS_COST = 65990;
    // - Unlock gas cost
    uint256 private immutable UNLOCK_COST;

    // Holding all pending cross chain transfer info
    // - ID of cross chain transfers
    uint256 internal crossChainTransfer;
    // - Enforce funding supply from users
    bool internal forceFunding = true;
    // - Outward transfer data
    mapping(uint256 => CrossChainTransfer) public outwardTransfers;
    // - Inward gas price funding, used as a flag for processed txs via `PROCESSED`
    mapping(bytes32 => uint256) public inwardTransferFunding;

    event CrossChainTransferLocked(address indexed from, uint256 i);
    event CrossChainTransferProcessed(
        address indexed to,
        uint256 amount,
        uint256 chain
    );
    event CrossChainUnlockFundsReceived(
        address indexed to,
        uint256 amount,
        uint256 chain,
        uint256 orderId
    );
    event CrossChainTransferUnlocked(
        address indexed to,
        uint256 amount,
        uint256 chain
    );

    modifier validChain(uint256 chain) {
        _validChain(chain);
        _;
    }

    modifier validFunding() {
        _validFunding();
        _;
    }

    constructor(uint256 unlockCost, uint256[] memory chainList)
        public
        Controller()
    {
        UNLOCK_COST = unlockCost;
        for (uint i = 0; i < chainList.length; i++) {
            isSupportedChain[chainList[i]] = true;
        }
    }

    function setForceFunding(bool _forceFunding) external onlyOwner() {
        forceFunding = _forceFunding;
    }

    function whiteListChain(uint256 _chainId, bool _whiteList) external onlyOwner() {
        isSupportedChain[_chainId] = _whiteList;
    }

    function process(uint256 i) external onlyOperator() {
        CrossChainTransfer memory cct = outwardTransfers[i];
        outwardTransfers[i].processed = true;
        if (forceFunding) msg.sender.sendValue(cct.gasPrice * PROCESS_COST);
        emit CrossChainTransferProcessed(cct.to, cct.amount, cct.chain);
    }

    function fundUnlock(
        uint256 satelliteChain,
        uint256 i,
        address to,
        uint256 amount
    ) external payable {
        uint256 funds = msg.value.div(UNLOCK_COST);
        require(
            funds != 0,
            "MultiBridge::fundUnlock: Incorrect amount of funds supplied"
        );
        bytes32 h = keccak256(abi.encode(satelliteChain, i, to, amount));
        require(
            inwardTransferFunding[h] != PROCESSED,
            "MultiBridge::fundUnlock: Transaction already unlocked"
        );
        require(
            inwardTransferFunding[h] == 0,
            "MultiBridge::fundUnlock: Funding already provided"
        );
        inwardTransferFunding[h] = funds;
        emit CrossChainUnlockFundsReceived(to, amount, satelliteChain, i);
    }

    function _validChain(uint256 chain) private view {
        require(
            isSupportedChain[chain],
            "MultiBridge::lock: Invalid chain specified"
        );
    }

    function _validFunding() private view {
        // Ensure sufficient funds accompany deposit to fund migration
        require(
            !forceFunding || tx.gasprice.mul(PROCESS_COST) <= msg.value,
            "MultiBridge::lock: Insufficient funds provided to fund migration"
        );
    }

    function checkTxProcessed(
        uint256 satelliteChain,
        uint256 i,
        address to,
        uint256 amount
    ) external view returns (bool) {
        return
            inwardTransferFunding[
                keccak256(abi.encode(satelliteChain, i, to, amount))
            ] == PROCESSED;
    }

    function lock(
        address to,
        uint256 amount,
        uint256 chain
    ) external payable virtual;

    function unlock(
        uint256 satelliteChain,
        uint256 i,
        address to,
        uint256 amount
    ) external virtual;

    function safe88(uint256 n, string memory errorMessage)
        internal
        pure
        returns (uint88)
    {
        require(n < 2**88, errorMessage);
        return uint88(n);
    }
}








// Code Based on Compound's Comp.sol: https://github.com/compound-finance/compound-protocol/blob/master/contracts/Governance/Comp.sol

contract ERC20MintSnapshot is ERC20 {
    /// @notice A checkpoint for marking number of mints from a given block
    struct Checkpoint {
        uint32 fromBlock;
        uint224 mints;
    }

    /// @notice A record of mint checkpoints for each account, by index
    mapping(address => mapping(uint32 => Checkpoint)) public checkpoints;

    /// @notice The number of checkpoints for each account
    mapping(address => uint32) public numCheckpoints;

    // Address to signify snapshotted total mint amount
    address private constant TOTAL_MINT = address(0);

    constructor(string memory name, string memory symbol)
        public
        ERC20(name, symbol)
    {}

    /**
     * @notice Determine the prior amount of mints for an account as of a block number
     * @dev Block number must be a finalized block or else this function will revert to prevent misinformation.
     * @param account The address of the account to check
     * @param blockNumber The block number to get the mint balance at
     * @return The amount of mints the account had as of the given block
     */
    function getPriorMints(address account, uint256 blockNumber)
        public
        view
        returns (uint224)
    {
        require(
            blockNumber < block.number,
            "ERC20MintSnapshot::getPriorMints: not yet determined"
        );

        uint32 nCheckpoints = numCheckpoints[account];
        if (nCheckpoints == 0) {
            return 0;
        }

        // First check most recent balance
        if (checkpoints[account][nCheckpoints - 1].fromBlock <= blockNumber) {
            return checkpoints[account][nCheckpoints - 1].mints;
        }

        // Next check implicit zero balance
        if (checkpoints[account][0].fromBlock > blockNumber) {
            return 0;
        }

        uint32 lower = 0;
        uint32 upper = nCheckpoints - 1;
        while (upper > lower) {
            uint32 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            Checkpoint memory cp = checkpoints[account][center];
            if (cp.fromBlock == blockNumber) {
                return cp.mints;
            } else if (cp.fromBlock < blockNumber) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return checkpoints[account][lower].mints;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        uint224 value =
            safe224(
                amount,
                "ERC20MintSnapshot::_beforeTokenTransfer: Amount minted exceeds limit"
            );
        if (from == address(0) && value > 0) {
            uint32 totalMintNum = numCheckpoints[TOTAL_MINT];
            uint224 totalMintOld =
                totalMintNum > 0
                    ? checkpoints[TOTAL_MINT][totalMintNum - 1].mints
                    : 0;
            uint224 totalMintNew =
                add224(
                    totalMintOld,
                    value,
                    "ERC20MintSnapshot::_beforeTokenTransfer: mint amount overflows"
                );
            _writeCheckpoint(TOTAL_MINT, totalMintNum, totalMintNew);

            uint32 minterNum = numCheckpoints[to];
            uint224 minterOld =
                minterNum > 0 ? checkpoints[to][minterNum - 1].mints : 0;
            uint224 minterNew =
                add224(
                    minterOld,
                    value,
                    "ERC20MintSnapshot::_beforeTokenTransfer: mint amount overflows"
                );
            _writeCheckpoint(to, minterNum, minterNew);
        }
    }

    function _writeCheckpoint(
        address minter,
        uint32 nCheckpoints,
        uint224 newMints
    ) internal {
        uint32 blockNumber =
            safe32(
                block.number,
                "ERC20MintSnapshot::_writeCheckpoint: block number exceeds 32 bits"
            );

        if (
            nCheckpoints > 0 &&
            checkpoints[minter][nCheckpoints - 1].fromBlock == blockNumber
        ) {
            checkpoints[minter][nCheckpoints - 1].mints = newMints;
        } else {
            checkpoints[minter][nCheckpoints] = Checkpoint(
                blockNumber,
                newMints
            );
            numCheckpoints[minter] = nCheckpoints + 1;
        }
    }

    function safe32(uint256 n, string memory errorMessage)
        internal
        pure
        returns (uint32)
    {
        require(n < 2**32, errorMessage);
        return uint32(n);
    }

    function safe224(uint256 n, string memory errorMessage)
        internal
        pure
        returns (uint224)
    {
        require(n < 2**224, errorMessage);
        return uint224(n);
    }

    function add224(
        uint224 a,
        uint224 b,
        string memory errorMessage
    ) internal pure returns (uint224) {
        uint224 c = a + b;
        require(c >= a, errorMessage);
        return c;
    }
}










contract FeeAccrual {
    using SafeMath for uint256;
    using SafeERC20 for ERC20MintSnapshot;

    Fees[] internal accruedFees;

    struct Fees {
        uint32 blockNumber;
        uint224 amount;
        mapping(address => bool) claimed;
    }

    // Token to pay out fees in and query about proportions
    ERC20MintSnapshot internal immutable wROOT;
    // Address to signify snapshotted total mint amount
    address private constant TOTAL_MINT = address(0);

    event CrossChainFeesClaimed(address indexed beneficiary, uint256 amount);

    constructor(ERC20MintSnapshot wroot) internal {
        wROOT = wroot;
    }

    function totalAvailableRedemption() external view returns (uint256 total) {
        for (uint256 i = 0; i < accruedFees.length; i++)
            total = total.add(availableRedemption(i));
    }

    function availableRedemption(uint256 num) public view returns (uint256) {
        Fees memory fees = accruedFees[num];

        if (accruedFees[num].claimed[msg.sender]) return 0;

        // uint256 userMints =
        //     uint256(wROOT.getPriorMints(msg.sender, fees.blockNumber));
        // uint256 totalMints =
        //     uint256(wROOT.getPriorMints(TOTAL_MINT, fees.blockNumber));
        
        uint256 amount = uint256(fees.amount);

        // redeem bridge fees based on total supply owned
        uint256 userMints =
            uint256(wROOT.balanceOf(msg.sender));
        uint256 totalMints =
            uint256(wROOT.totalSupply());

        return amount.mul(userMints).div(totalMints);
    }

    function redeemAll() external {
        uint256 availableFees = 0;
        for (uint256 i = 0; i < accruedFees.length; i++) {
            uint256 available = availableRedemption(i);
            if (available > 0) {
                availableFees = availableFees.add(available);
                accruedFees[i].claimed[msg.sender] = true;
            }
        }
        require(availableFees > 0, "FeeAccrual::redeem: No fees are claimable");

        wROOT.safeTransfer(msg.sender, availableFees);

        emit CrossChainFeesClaimed(msg.sender, availableFees);
    }

    function redeem(uint256 num) external {
        uint256 availableFees = availableRedemption(num);

        require(
            availableFees != 0,
            "FeeAccrual::redeem: No fees are claimable"
        );

        accruedFees[num].claimed[msg.sender] = true;
        wROOT.safeTransfer(msg.sender, availableFees);

        emit CrossChainFeesClaimed(msg.sender, availableFees);
    }

    function safe32(uint256 n, string memory errorMessage)
        internal
        pure
        returns (uint32)
    {
        require(n < 2**32, errorMessage);
        return uint32(n);
    }
}












/**
 * wROOT MainMultiBridge
 *
 * Attributes:
 * - Locks wROOT tokens and supplies
 * - Unlocks wROOT tokens from an authorized member after a cross-chain migration
 * - Powered by Prismnetwork.io
 */
contract MainMultiBridge is MultiBridge, FeeAccrual {
    using SafeERC20 for ERC20MintSnapshot;

    // Gas Units Required for an `unlock`
    uint256 private constant MAIN_UNLOCK_COST = 81574;

    event CrossChainFeeAccrual(uint256 amount);

    constructor(ERC20MintSnapshot wroot, uint256[] memory chainList)
        public
        MultiBridge(MAIN_UNLOCK_COST, chainList)
        FeeAccrual(wroot)
    {}

    function lock(
        address to,
        uint256 amount,
        uint256 chain
    ) external payable override validFunding() validChain(chain) {
        // Zero Security Assumptions
        uint256 lockAmount = wROOT.balanceOf(address(this));
        wROOT.safeTransferFrom(msg.sender, address(this), amount);
        lockAmount = wROOT.balanceOf(address(this)).sub(lockAmount);

        uint256 id = crossChainTransfer++;

        outwardTransfers[id] = CrossChainTransfer(
            to,
            false,
            safe88(
                tx.gasprice,
                "Multibridge::lock: tx gas price exceeds 32 bits"
            ),
            amount,
            chain
        );

        // Optionally captured by off-chain migrator
        emit CrossChainTransferLocked(msg.sender, id);
    }

    function unlock(
        uint256 satelliteChain,
        uint256 i,
        address to,
        uint256 amount
    ) external override onlyOperator() {
        bytes32 h = keccak256(abi.encode(satelliteChain, i, to, amount));

        uint256 refundGasPrice = inwardTransferFunding[h];
        if (refundGasPrice == PROCESSED) return;
        inwardTransferFunding[h] = PROCESSED;

        wROOT.safeTransfer(to, amount);
        if (refundGasPrice != 0)
            msg.sender.sendValue(refundGasPrice * MAIN_UNLOCK_COST);

        emit CrossChainTransferUnlocked(to, amount, satelliteChain);
    }

    // Fee handling
    function accrueFees(uint224 amount) external onlyOwner() {
        accruedFees.push(
            Fees(
                safe32(
                    block.number,
                    "Multibridge::accrueFees: block number exceeds 32 bits"
                ),
                amount
            )
        );
        emit CrossChainFeeAccrual(amount);
    }

    function transferBridgeFees(uint256 amount) external onlyOwner() {
        wROOT.safeTransfer(msg.sender, amount);
    }
}
