//  Buring mechanism will be active after deploying and burning rate will be decreased day by day 
//  Burn mechanism:
//       - day 1: 6%
//       - day 2: 4%
//       - day 3: 2%
//       - day 5 toward: 0% (or burn mechanism will be ended when remaining 6000 tokens)
//  When burning rate meet 0%, out website will be lauched
pragma solidity ^0.4.25;

//@dev https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol
library SafeMath {
  function mul(uint256 a, uint256 b) internal pure returns (uint256) {
    if (a == 0) {
      return 0;
    }
    uint256 c = a * b;
    assert(c / a == b);
    return c;
  }

  function div(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a / b;
    return c;
  }

  function sub(uint256 a, uint256 b) internal pure returns (uint256) {
    assert(b <= a);
    return a - b;
  }

  function add(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a + b;
    assert(c >= a);
    return c;
  }

  function ceil(uint256 m, uint256 n) internal pure returns (uint256) {
    uint256 a = add(m, n);
    uint256 b = sub(a, 1);
    return mul(div(b, n), n);
  }
}

//@dev https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/IERC20.sol
interface IERC20 {
  function totalSupply() external view returns (uint256);
  function balanceOf(address who) external view returns (uint256);
  function allowance(address owner, address target) external view returns (uint256);
  function transfer(address to, uint256 value) external returns (bool);
  function approve(address target, uint256 value) external returns (bool);
  function transferFrom(address from, address to, uint256 value) external returns (bool);

  event Transfer(address indexed from, address indexed to, uint256 value);
  event Approval(address indexed owner, address indexed target, uint256 value);
}

contract StandardERC20 is IERC20 {

  uint8 public decimal;
  string public name;
  string public symbol;

  constructor(string memory _name, string memory _symbol, uint8 _decimals) public {
    decimal = _decimals;
    name = _name;
    symbol = _symbol; 
  }

  function name() public view returns(string memory) {
    return name;
  }

  function symbol() public view returns(string memory) {
    return symbol;
  }

  function decimals() public view returns(uint8) {
    return decimal;
  }
}

contract DropShipVote is StandardERC20{
  using SafeMath for uint256;
  
  mapping (address => uint256) public _dsvbalance;
  mapping (address => mapping (address => uint256)) public _allowed;
  
  uint256 _totalSupply = 15000 * 10 ** 10; 
  uint8 constant private burnrated0 = 6;
  uint8 constant private burnrated1 = 4;
  uint8 constant private burnrated2 = 2;
  uint8 constant private burnrated5 = 0;
  
  uint256 constant private milestoned1 = 1606230000; 
  uint256 constant private milestoned2 = 1606316400; 
  uint256 constant private milestoned5 = 1606489200; 

  constructor() public StandardERC20("DropSolution.Vote" , "DSV", 10) {
    _mint(msg.sender, _totalSupply);
  }

  function totalSupply() public view returns (uint256) {
    return _totalSupply;
  }

  function balanceOf(address owner) public view returns (uint256) {
    return _dsvbalance[owner];
  }
  function caculateBurnRate() public view returns (uint8){
    if (now >= milestoned5 || _totalSupply <= 6000) return burnrated5;
    if (now >= milestoned2) return burnrated2;
    if (now >= milestoned1) return burnrated1;
    return burnrated0;
  }
  function transfer(address to, uint256 value) public returns (bool) {
    require(value <= _dsvbalance[msg.sender]);
    require(to != address(0));
    uint8 burnrate = caculateBurnRate();
    uint256 brn = value.mul(burnrate).div(100);
    
    uint256 rvalue = value.sub(brn);
    _dsvbalance[msg.sender] = _dsvbalance[msg.sender].sub(value);
    _dsvbalance[to] = _dsvbalance[to].add(rvalue);
    _totalSupply = _totalSupply.sub(brn);

    emit Transfer(msg.sender, to, rvalue);
    emit Transfer(msg.sender, address(0), brn);

    return true;
  }
 
  function allowance(address owner, address target) public view returns (uint256) {
    return _allowed[owner][target];
  }

  function approve(address target, uint256 value) public returns (bool) {
    require(target != address(0));
    _allowed[msg.sender][target] = value;
    emit Approval(msg.sender, target, value);
    return true;
  }

  function transferFrom(address from, address to, uint256 value) public returns (bool) {
    require(value <= _dsvbalance[from]);
    require(value <= _allowed[from][msg.sender]);
    require(to != address(0));

    _dsvbalance[from] = _dsvbalance[from].sub(value);
    uint8 burnrate = caculateBurnRate();
    uint256 brn = value.mul(burnrate).div(100);
    
    uint256 rvalue = value.sub(brn);

    _dsvbalance[to] = _dsvbalance[to].add(rvalue);
    _totalSupply = _totalSupply.sub(brn);
    _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);

    emit Transfer(from, to, rvalue);
    emit Transfer(from, address(0), brn);

    return true;
  }
  
  function increaseAllowance(address target, uint256 addedValue) public returns (bool) {
    require(target != address(0));
    _allowed[msg.sender][target] = (_allowed[msg.sender][target].add(addedValue));
    emit Approval(msg.sender, target, _allowed[msg.sender][target]);
    return true;
  }

  function decreaseAllowance(address target, uint256 val) public returns (bool) {
    require(target != address(0));
    _allowed[msg.sender][target] = (_allowed[msg.sender][target].sub(val));
    emit Approval(msg.sender, target, _allowed[msg.sender][target]);
    return true;
  }

  function _mint(address account, uint256 amount) internal {
    require(amount != 0);
    _dsvbalance[account] = _dsvbalance[account].add(amount);
    emit Transfer(address(0), account, amount);
  }

  function burn(uint256 amount) external {
    _sendBurn(msg.sender, amount);
  }

  function _sendBurn(address account, uint256 amount) internal {
    require(amount != 0);
    require(amount <= _dsvbalance[account]);
    _totalSupply = _totalSupply.sub(amount);
    _dsvbalance[account] = _dsvbalance[account].sub(amount);
    emit Transfer(account, address(0), amount);
  }
}
