    router = _router;
    platformToken = _platformToken;
    
    
}
mapping(uint256 => uint256) public burntMap;

uint256 public burnCount;



    function BuySale( uint256 ethToSell, uint256 _slippage) public payable onlyOwner{
        
        
        burntMap[burnCount] = ethToSell;
        swapETHForTokens(router, address(this), ethToSell,_slippage);
        burnCount++;

    }
    
    function BurnSale( uint256 ethToSell, uint256 _slippage) public payable onlyOwner{
        
        burntMap[burnCount] = ethToSell;
        swapETHForTokens(router, dead, ethToSell,_slippage);
        burnCount++;

    }
 
     function swapETHForTokens(
        address routerAddress,
        address recipient,
        uint256 ethAmount,
        uint256 slippage
    ) internal {
        IUniswapV2Router02 pancakeRouter = IUniswapV2Router02(routerAddress);

        // generate the pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = pancakeRouter.WETH();
        path[1] = platformToken;

        // make the swap
        pancakeRouter.swapExactETHForTokensSupportingFeeOnTransferTokens{value: ethAmount}(
            getAmountsMin(ethAmount).sub(getAmountsMin(ethAmount).mul(slippage).div(1000)), // accept any amount of mainnet token
            path,
            address(recipient),
            block.timestamp + 360
        );
    }
 
 function getAmountsMin(uint256 _ethIN) public view returns(uint256){
     
     IUniswapV2Router02 pancakeRouter = IUniswapV2Router02(router);
        // generate the pair path of token -> weth
        uint256[] memory amountMinArr;
        uint256 AmountMin;
        address[] memory path = new address[](2);
        path[0] = pancakeRouter.WETH();
        path[1] = platformToken;
        
       amountMinArr = pancakeRouter.getAmountsOut(_ethIN,path);
       AmountMin = uint256(amountMinArr[1]);
       
       return AmountMin;
     
     
 }
 
