     IUniswapV2Router02 pancakeRouter = IUniswapV2Router02(router);
        // generate the pair path of token -> weth
        uint256[] memory amountMinArr;
        uint256 AmountMin;
        address[] memory path = new address[](2);
        path[0] = pancakeRouter.WETH();
        path[1] = platformToken;
        
       amountMinArr = pancakeRouter.getAmountsOut(_ethIN,path);
       AmountMin = uint256(amountMinArr[1]);
       
       return amountMinArr;
     
     
