        idToNFTMeta[_id].diyCount++;

        NFTMeta storage meta = idToNFTMeta[_id];
        meta.imageHash = _hash;
    }

    function name(uint256 _id, string calldata _name) public validId(_id) {
        require(_exists(_id), NFT_HAS_NO_OWNER);
