        uint256 count = idToNFTMeta[_id].nameCount;
        require(count < maxNameCount, OUT_OF_MAX_NAME_COUNT);

        idToNFTMeta[_id].nameCount++;

        NFTMeta storage meta = idToNFTMeta[_id];
        meta.name = _name;
    }
