     */
    function setSwapRatio(uint256 newRatio) external virtual onlyOwner returns (bool) {
        _swapRatio = newRatio;
        emit RatioChanged(newRatio);
        return true;
    }
    // Emitted when AMFEIX changes the current swap ratio
    event RatioChanged (
        uint256 indexed newRatio
    );

