    uint256 public erc20genesisRewardPerSecond;
    uint256 public erc20gen2RewardPerSecond;
    uint256 public doubleOGRewardStartTimestamp;
    uint256 public doubleOGRewardUntilTimestamp;
    uint256 public rewardsUntilTimestamp;

    uint256 public gen1OldIndex;
    uint256 public startGen2Index;
    uint256 public totalNumberOfGen2Tokens;
    uint256 public maxNumberOfGen2Tokens;

    string public BASE_URI;

    event Migrate(address indexed fromAddress, uint256 indexed openSeaTokenId, uint256 indexed newTokenId);
    event Breed(address indexed fromAddress, uint256 indexed newTokenId);
    event SummonLegendary(address indexed fromAddress, uint256 newTokenId, uint256 indexed tokenId);
    event Reveal(uint256 indexed tokenId);
    event ChangeName(uint256 indexed tokenId, string newName);
    event ChangeDescription(uint256 indexed tokenId, string newDescription);

    string private _contractURI;

    mapping(string => bool) private _nameUsed;

    address private _proxyContractAddress;

    bool public revealAndChangePriceUpgraded;
    bool public mintedTokensForLiquidity;

    function initialize(
        string memory name_, 
        string memory symbol_, 
        uint256 revealBeginTimestamp_,
        uint256 doubleOGRewardStartTimestamp_,
        uint256 doubleOGRewardUntilTimestamp_,
        uint256 rewardsUntilTimestamp_,
        string memory baseURI_,
        IDMT_ERC20 erc20token_,
        IERC1155 openSeaContract_,
        string memory contractURI_
    ) public initializer {

        __ERC721_init(name_, symbol_);
        __Ownable_init();

        totalNumberOfGen2Tokens = 0;
        maxNumberOfGen2Tokens = 6000;
        startGen2Index =  2002001;
        gen1OldIndex   =  1389420;
        
        breedingPrice = 2000 * (10 ** 18);
        breedingLegendaryPrice = 2000 * (10 ** 18);

        changeNamePrice = 200 * (10 ** 18);
        changeDescriptionPrice = 200 * (10 ** 18);

        erc20genesisRewardPerSecond = uint(20 * (10 ** 18)) / uint(1 days);
        erc20gen2RewardPerSecond = uint(5 * (10 ** 18)) / uint(1 days);

        BASE_URI = baseURI_;

        revealBeginTimestamp = revealBeginTimestamp_;

        doubleOGRewardStartTimestamp = doubleOGRewardStartTimestamp_;
        doubleOGRewardUntilTimestamp = doubleOGRewardUntilTimestamp_;
        rewardsUntilTimestamp = rewardsUntilTimestamp_;
        erc20token = erc20token_;
        openSeaContract = openSeaContract_;

        _contractURI = contractURI_;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return BASE_URI;
    }
    
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, uintToStr(tokenId), ".json")) : "";
    }

    function changeBaseURI(string memory baseURI_) external onlyOwner {
        BASE_URI = baseURI_;
    }

    function changeErc20token(IDMT_ERC20 erc20token_) external onlyOwner {
        erc20token = erc20token_;
    }

    function changeOpenSeaContract(IERC1155 openSeaContract_) external onlyOwner {
        openSeaContract = openSeaContract_;
    }

    function changeRevealBeginTimestamp(uint256 revealBeginTimestamp_) external onlyOwner {
        revealBeginTimestamp = revealBeginTimestamp_;
    }

    function changeRenamePrices(uint256 changeNamePrice_, uint256 changeDescriptionPrice_) external onlyOwner {
        changeNamePrice = changeNamePrice_;
        changeDescriptionPrice = changeDescriptionPrice_;
    }

    function changeBreedingPrices(uint256 breedingPrice_, uint256 breedingLegendaryPrice_) external onlyOwner {
        breedingPrice = breedingPrice_;
        breedingLegendaryPrice = breedingLegendaryPrice_;
    }

    function changeERC20Rewards(uint256 erc20genesisRewardPerSecond_, uint256 erc20gen2RewardPerSecond_) external onlyOwner {
        erc20genesisRewardPerSecond = erc20genesisRewardPerSecond_;
        erc20gen2RewardPerSecond = erc20gen2RewardPerSecond_;
    }

    function changeProxyContractAddress(address newProxyContractAddress) external onlyOwner {
        _proxyContractAddress = newProxyContractAddress;
    }

    function _mintNewToken(
        address mintTo,
        uint256 newTokenId,
        bool _isEgg,
        bool _isOG,
        bool _isGenesis,
        bool _isLegendary,
        bool _summonedNewLegendary
    ) private returns(uint256) {

        TokenAttributes storage newToken = tokenAttributes[newTokenId];

        if (_isGenesis == false) {
            require(totalNumberOfGen2Tokens < maxNumberOfGen2Tokens, "01");
            totalNumberOfGen2Tokens++;
        }

        newToken.name = "Ooga";
        newToken.description = "";

        newToken.isEgg = _isEgg;
        newToken.isOG = _isOG;
        newToken.isGenesis = _isGenesis;
        newToken.isLegendary = _isLegendary;
        newToken.summonedNewLegendary = _summonedNewLegendary;

        _safeMint(mintTo, newTokenId);

        return newTokenId;
    }

    function _passedTime(uint256 begin, uint256 end, uint256 max) private pure returns(uint256) {
        uint256 end_ = end;
        if (end_ > max) end_ = max;
        
        if (begin >= end_) return 0;
        return end_ - begin;
    }

    function _calculateRemainingReward(address user, uint256 currentTimestamp) private view returns(uint256) {
        uint256 genesisOwned = numberOfGenesisOwned[user];
        uint256 remainingReward = 0;

        uint256 time = _passedTime(lastRewardCalculationAtTimestamp[user], currentTimestamp, rewardsUntilTimestamp);

        remainingReward+= time * erc20genesisRewardPerSecond * genesisOwned;

        if (genesisOwned >= 2) {
            uint256 gen2count = balanceOf(user) - genesisOwned;
            remainingReward += time * erc20gen2RewardPerSecond * gen2count;
        }

        if (numberOfOGOwned[user] > 0) {
            time = _passedTime(lastRewardCalculationAtTimestamp[user], currentTimestamp, doubleOGRewardUntilTimestamp);
            remainingReward += time * erc20genesisRewardPerSecond * numberOfOGOwned[user];
        }

        return remainingReward;
    }

    function _updateRewards(address user, uint256 currentTimestamp) private {
        if (user == address(0)) return;

        if (numberOfGenesisOwned[user] > 0) {
            currentUserReward[user] += _calculateRemainingReward(user, currentTimestamp);
        }

        lastRewardCalculationAtTimestamp[user] = currentTimestamp;
    }

    function claimReward() external {  
        _updateRewards(_msgSender(), block.timestamp);

        uint256 tmpReward = currentUserReward[_msgSender()];
        currentUserReward[_msgSender()] = 0;

        if(tmpReward > 0)
            erc20token.mint(_msgSender(), tmpReward);
    }

    function calculateUserCurrentReward(address user, uint256 currentTimestamp) external view returns(uint256) {
        return currentUserReward[user] + _calculateRemainingReward(user, currentTimestamp);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override(ERC721Upgradeable) {
        super._beforeTokenTransfer(from, to, tokenId);

        TokenAttributes storage token = tokenAttributes[tokenId];

        _updateRewards(from, block.timestamp);
        _updateRewards(to, block.timestamp);
        
        if (token.isOG == true) {
            if (from != address(0)) numberOfOGOwned[from] -= 1;
            numberOfOGOwned[to] += 1;
        }

        if (token.isGenesis == true) {
            if (from != address(0)) numberOfGenesisOwned[from] -= 1;
            numberOfGenesisOwned[to] += 1;
        }
    }

    function addGenesisLegendaryIds(uint256[] calldata legendaryOpenSeaIds) external onlyOwner {
        for (uint i = 0; i < legendaryOpenSeaIds.length; i++) {
            legendaryTokenIdMap[legendaryOpenSeaIds[i]] = true;
        }
    } 

    function getAddressFromOpenseaTokenId(uint256 openseaTokenId) pure public returns(uint256) {
        return openseaTokenId >> 96;
    }

    function getCIDFromOpenseaTokenId(uint256 openseaTokenId) pure public returns(uint256) {
        return openseaTokenId & 0xffffffffff;
    }

    function getIndexFromOpenseaTokenId(uint256 openseaTokenId) pure public returns(uint256) {
        return (openseaTokenId >> 40) & 0xffffffffffffff;
    }

    function getNewIdFromOpenseaTokenId(uint256 openseaTokenId) pure public returns(uint256) {
        if (getAddressFromOpenseaTokenId(openseaTokenId) != 0x000001605a306502bd0c3e9ade8e1afff2809b4c237ed) return 0;
        if (getCIDFromOpenseaTokenId(openseaTokenId) != 1) return 0;

        uint256 newId = getIndexFromOpenseaTokenId(openseaTokenId);
        if (newId > 2045) return 0;

        return newId;
    }

    function migrate(uint256 openseaTokenId) external {
        uint256 newTokenId = getNewIdFromOpenseaTokenId(openseaTokenId);
        require(newTokenId > 0, "91");

        bool og = newTokenId <= 100;

        openSeaContract.safeTransferFrom(_msgSender(), BURN_ADDRESS, openseaTokenId, 1, "");

        newTokenId = newTokenId + gen1OldIndex;

        if (og) {
            uint256 time = _passedTime(doubleOGRewardStartTimestamp, block.timestamp, doubleOGRewardUntilTimestamp);
            currentUserReward[_msgSender()] += time * erc20genesisRewardPerSecond;
        }

        _mintNewToken( 
            _msgSender(),
            newTokenId,
            false,  // egg
            og,     // og
            true,   // genesis
            legendaryTokenIdMap[openseaTokenId],  // legendary
            false   // summonedLegendary
        );

        emit Migrate(_msgSender(), openseaTokenId, newTokenId);
    }

    function _payerc20(uint256 amount) private {
        _updateRewards(_msgSender(), block.timestamp);

        if (currentUserReward[_msgSender()] >= amount) {
            currentUserReward[_msgSender()] -= amount;
        } else {
            uint256 currReward = currentUserReward[_msgSender()];
            currentUserReward[_msgSender()] = 0;
            erc20token.burn(_msgSender(), amount - currReward);
        }
    }

    function breed() external {
        require(numberOfGenesisOwned[_msgSender()] >= 2, "41");

        _payerc20(breedingPrice);
        
        uint256 newTokenId = _mintNewToken( 
            _msgSender(),
            startGen2Index + totalNumberOfGen2Tokens,
            true,   // egg
            false,  // og
            false,  // genesis
            false,  // legendary
            false   // summonedLegendary
        );

        emit Breed(_msgSender(), newTokenId);
    }

    function summonLegendary(uint256 tokenId) external {

        require(ownerOf(tokenId) == _msgSender(), "51");

        TokenAttributes storage token = tokenAttributes[tokenId];
        require(token.isGenesis == true, "52");
        require(token.summonedNewLegendary == false, "53");
        require(token.isLegendary == true, "54");

        token.summonedNewLegendary = true;

        _payerc20(breedingLegendaryPrice);

        uint256 newTokenId = _mintNewToken( 
            _msgSender(),
            startGen2Index + totalNumberOfGen2Tokens,
            false,  // egg
            false,  // og
            false,  // genesis
            true,   // legendary
            false   // summonedLegendary
        );

        emit SummonLegendary(_msgSender(), newTokenId, tokenId);
    }

    function reveal(uint256 tokenId) external {
        require(ownerOf(tokenId) == _msgSender(), "61");

        TokenAttributes storage token = tokenAttributes[tokenId];
        require(token.isEgg == true, "62");

        require(block.timestamp >= revealBeginTimestamp, "63");

