// File contracts/ethereum/token/BicoClaimNew.sol


pragma solidity ^0.8.0;

//import "./BiconomyToken.sol";





//TODO
//make upgradeable again
//clean up
//add pausability modifier and reentrancy guard
//remove constrctor and setup proper initializer
//check sample audit reports and review security for all contracts
//trasnfer limits, max claim limit etc
contract BicoClaimNew is AccessProtected, Pausable, ReentrancyGuard {
    using ECDSA for bytes32;
    struct Voucher {
        address recipient;
        uint256 amount;
        bytes signature;
    }

    uint256 public bicoHardLimit = 10000*1e18;

    bytes32 internal constant EIP712_DOMAIN_TYPEHASH = keccak256(bytes("EIP712Domain(string name,string version,address verifyingContract,bytes32 salt)"));
    bytes32 public constant VOUCHER_TYPEHASH = keccak256(bytes("Voucher(address recipient,uint256 amount)"));
    bytes32 internal domainSeparator;
    address public tokenAddress;

    mapping(address => uint256) public totalTokensClaimedByUser; // total $BICO tokens claimed per address
    
    event BicoClaimed(address indexed _user,uint256 _claimedBico);
    
    constructor(address _tokenAddress, string memory name, string memory version) {
        require(_tokenAddress != address(0),"Token address can not be zero");
        tokenAddress = _tokenAddress;
        domainSeparator = keccak256(abi.encode(
            EIP712_DOMAIN_TYPEHASH,
            keccak256(bytes(name)),
            keccak256(bytes(version)),
            address(this),
            bytes32(getChainID())
        ));
    }

    // returns total number of bico tokens claimed so far from user
    function getTokensClaimedByUser(address user) public view returns (uint256) {
        return totalTokensClaimedByUser[user];
    }

    /**
     * claim voucher bico tokens
     *
     * returns number of bico claimed
     */
    function claimVoucher(Voucher calldata _voucher) external whenNotPaused nonReentrant returns (uint256) {
        address recipient = _msgSender();

        address signer = _verify(_voucher);
        uint256 claimedAmount = totalTokensClaimedByUser[recipient]; // total claimed bico

        require(recipient == _voucher.recipient, "TokenClaim: msg.sender does not match recipient");
        require(_voucher.amount > claimedAmount, "TokenClaim: No $BICO left to claim");

        require(signer != address(0), "TokenClaim: Unable to recover signer from signature");
        require(isAdmin(signer) || signer == owner(), "TokenClaim: Invalid Voucher, not signed by admin or owner");
        require(recipient == _voucher.recipient, "TokenClaim: This Voucher does not belong to msg.sender");
        
        uint256 claimable = (_voucher.amount - claimedAmount)*1e18;
        require(claimable < bicoHardLimit,"voucher claim amount exceeds the hard-cap");

        // set bico to latest amount on voucher
        totalTokensClaimedByUser[recipient] = _voucher.amount;

        // transfer tokens to user
        IERC20(tokenAddress).transfer(recipient, claimable);
        emit BicoClaimed(recipient,claimable);
        return claimable;
    }

    function _verify(Voucher memory voucher) internal view returns (address) {
        bytes32 digest =
            keccak256(abi.encodePacked(
                "\x19\x01",
                getDomainSeparator(),
                keccak256(abi.encode(VOUCHER_TYPEHASH,
                            voucher.recipient,
                            voucher.amount
                        ))));
        return digest.recover(voucher.signature);
    }

    /**
     * Withdraw ERC20 Tokens from the Token Claim contract address
     *
     */
    function withdrawTokens(address wallet, uint256 amount) external onlyOwner nonReentrant {
        require(amount > 0, "Nothing to withdraw");
        IERC20(tokenAddress).transfer(wallet, amount);
    }
    
    function getDomainSeparator() internal view returns(bytes32) {
        return domainSeparator;
    }

    /** set proxy registry address
     */
    function setHardCap(uint256 _hardLimit) external onlyOwner {
        require(_hardLimit > 0,"Must be greater than zero");
        bicoHardLimit = _hardLimit;
    }


    function getChainID() public view returns (uint256 id) {
        assembly {
            id := chainid()
        }
    }

    function pause() external onlyAdmin {
        _pause();
    }

    function unpause() external onlyAdmin {
        _unpause();
    }
}
