// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./utils/Address.sol";
import "./utils/Context.sol";

/*
How to swap tokens

Aca hay 18 ceros: 000000000000000000
*/

contract AOLibreSwap {
    event PaymentReleased(address to, uint256 amount);
    event PriceTokenChanged(uint256 amount);

    address public TokenBankAddress;
    address public ContractOwner;
    IERC20 public AOLToken;
    uint256 public AOLTokenPrice;

    constructor() {
        AOLToken = IERC20(0x859C8159bC53022354f39357BD1FDF6091542087);
        TokenBankAddress = 0x334c1314EEbA22Ef4b123708403FC9640d2d69c3;
        AOLTokenPrice = 330000000000000;
        ContractOwner = msg.sender;
    }

    function setPriceToken(uint256 tokenPrice) public virtual {
        require(ContractOwner == msg.sender, "AOLibre: GET OUT, YOU ARE NOT THE OWNER");
        AOLTokenPrice = tokenPrice;

        emit PriceTokenChanged(AOLTokenPrice);
    }

    /**
     * @dev Triggers a transfer to `account` of the amount of Ether they are owed, according to their percentage of the
     * total shares and their previous withdrawals.
     */
    function withdraw() public virtual {
        require(msg.sender == ContractOwner, "No sos el owner, vola de aca");

        uint256 payment = address(this).balance;

        address payable account = payable(msg.sender);

        require(payment != 0, "AOLibre: You are the man, but you are not due payment");

        Address.sendValue(account, payment);
        emit PaymentReleased(account, payment);
    }

    function buy() payable public {
        uint256 amountTobuy = msg.value * 10**18;
        //El precio de msg.value esta en WEI, hay que agregarle los 18 ceros..

        //330000000000000
        //uint256 AOLTokenPrice = 33e13;

        require(amountTobuy >= AOLTokenPrice, "Not enough ethereum to make the transaction, we need more money!! / No tenes dinero/ethereum en tu billetera.");
        uint256 bankBalance = AOLToken.balanceOf(TokenBankAddress);

        uint256 tokensToSend = amountTobuy / AOLTokenPrice;
        require(tokensToSend <= bankBalance, "Not enough tokens in the reserve / No hay suficientes tokens se acabaron.");

        _safeTransferFrom(AOLToken, TokenBankAddress, msg.sender, tokensToSend);
    }

    function _safeTransferFrom(
        IERC20 token,
        address sender,
        address recipient,
        uint amount
    ) private {
        bool sent = token.transferFrom(sender, recipient, amount);
        require(sent, "Token transfer failed");
    }

    function ethBalance () public view returns (uint256) {
        return address(this).balance;
    }
}

