contracts/token/MultiSignature.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.7.6;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "../core/SafeOwnable.sol";

contract MultiSignature is AccessControl {
    using SafeERC20 for IERC20;

    event NewReceiver(address _oldReceiver, address _newReceiver);
    event ApplyToken(address requester, uint amount, uint requestAt, uint expireAt, bytes32 salt);
    event AcceptApplyToken(address requester, uint amount, uint requestAt, uint expireAt, bytes32 salt, address acceptor, uint currentNum);
    event ProcessApplyToken(address requester, uint amount, uint requestAt, uint expireAt, bytes32 salt);
    event ApplySetReceiver(address requester, address receiver, uint requestAt, uint expireAt, bytes32 salt);
    event AcceptApplySetReceiver(address requester, address receiver, uint requestAt, uint expireAt, bytes32 salt, address acceptor, uint currentNum);
    event ProcessApplySetReceiver(address requester, address receiver, uint requestAt, uint expireAt, bytes32 salt);

    bytes32 public constant SIGNER_ROLE = keccak256("MULTI_SIGNATURE_SIGNER");

    address public receiver;
    IERC20 immutable reward;
    mapping(bytes32 => bool) public transactions;
    mapping(bytes32 => uint) public signatureNum;
    mapping(bytes32 => mapping(address => bool)) signatureRecord;
    uint256 public immutable SIGNATURE_MIN_NUM;

    constructor(IERC20 _reward, address _receiver, address[] memory _signaturer, uint _minNum) {
        reward = _reward; 
        require(_receiver != address(0), "receiver address is zero");
        receiver = _receiver;
        emit NewReceiver(address(0), receiver);
        for (uint256 i = 0; i < _signaturer.length; ++i) {
            require(!hasRole(SIGNER_ROLE, _signaturer[i]), "address already signature");
            _setupRole(SIGNER_ROLE, _signaturer[i]);
        }
        require(_minNum > 0 && _minNum <= _signaturer.length, "illegal minNum");
        SIGNATURE_MIN_NUM = _minNum;
    }

    function setSignature(address _newSignature) external {
        require(hasRole(SIGNER_ROLE, _msgSender()), "caller is not a signature");
        renounceRole(SIGNER_ROLE, _msgSender());
        _setupRole(SIGNER_ROLE, _newSignature);
    }

    function applyToken(uint amount, uint expireAt, bytes32 salt) external {
        bytes32 hash = keccak256(abi.encode(_msgSender(), amount, block.timestamp, expireAt, salt));
        require(!transactions[hash], "apply already request");
        transactions[hash] = true;
        signatureNum[hash] = 0;
        emit ApplyToken(msg.sender, amount, block.timestamp, expireAt, salt);
    }

    function acceptApplyToken(address _requester, uint _amount, uint _requestAt, uint _expireAt, bytes32 _salt) external {
        require(block.timestamp >= _requestAt && block.timestamp <= _expireAt, "illegal time");
        require(hasRole(SIGNER_ROLE, _msgSender()), "caller is not a signature");
        bytes32 hash = keccak256(abi.encode(_requester, _amount, _requestAt, _expireAt, _salt));
        require(transactions[hash], "apply not exists");
        require(!signatureRecord[hash][_msgSender()], "signature already accepted");
        signatureNum[hash] = signatureNum[hash] + 1;
        signatureRecord[hash][_msgSender()] = true;
        emit AcceptApplyToken(_requester, _amount, _requestAt, _expireAt, _salt, _msgSender(), signatureNum[hash]);
        if (signatureNum[hash] >= SIGNATURE_MIN_NUM) {
            reward.safeTransfer(receiver, _amount); 
            delete transactions[hash];
            delete signatureNum[hash];
            emit ProcessApplyToken(_requester, _amount, _requestAt, _expireAt, _salt);
        }
    }

    function applySetReceiver(address _receiver, uint expireAt, bytes32 salt) external {
        require(_receiver != address(0) && _receiver != receiver, "receiver is zero or the same");
        bytes32 hash = keccak256(abi.encode(_msgSender(), _receiver, block.timestamp, expireAt, salt));
        require(!transactions[hash], "apply already request");
        transactions[hash] = true;
        signatureNum[hash] = 0;
        emit ApplySetReceiver(msg.sender, _receiver, block.timestamp, expireAt, salt);
    }

    function acceptApplySetReceiver(address _requester, address _receiver, uint _requestAt, uint _expireAt, bytes32 _salt) external {
        require(block.timestamp >= _requestAt && block.timestamp <= _expireAt, "illegal time");
        require(hasRole(SIGNER_ROLE, _msgSender()), "caller is not a signature");
        bytes32 hash = keccak256(abi.encode(_requester, _receiver, _requestAt, _expireAt, _salt));
        require(transactions[hash], "apply not exists");
        require(!signatureRecord[hash][_msgSender()], "signature already accepted");
        signatureNum[hash] = signatureNum[hash] + 1;
        signatureRecord[hash][_msgSender()] = true;
        emit AcceptApplySetReceiver(_requester, _receiver, _requestAt, _expireAt, _salt, _msgSender(), signatureNum[hash]);
        if (signatureNum[hash] >= SIGNATURE_MIN_NUM) {
            emit NewReceiver(receiver, _receiver);
            receiver = _receiver;
            delete transactions[hash];
            delete signatureNum[hash];
            emit ProcessApplySetReceiver(_requester, _receiver, _requestAt, _expireAt, _salt);
        }
    }
}

contracts/mock/swap/TransferHelper.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;

import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(address token, address to, uint value) internal {
        SafeERC20.safeApprove(IERC20(token), to, value);
    }

    function safeTransfer(address token, address to, uint value) internal {
        SafeERC20.safeTransfer(IERC20(token), to, value);
    }

    function safeTransferFrom(address token, address from, address to, uint value) internal {
        SafeERC20.safeTransferFrom(IERC20(token), from, to, value);
    }

    function safeTransferETH(address to, uint value) internal {
        (bool success,) = to.call{value:value}(new bytes(0));
        require(success, 'TransferHelper: ETH_TRANSFER_FAILED');
    }
}

contracts/mock/swap/MockRouter.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '../../interfaces/IWETH.sol';
import "../../core/SafeOwnable.sol";
import './TransferHelper.sol';
import './ISwapMining.sol';
import './IMockFactory.sol';
import './IMockRouter.sol';
import './MockLibrary.sol';

contract MockRouter is IMockRouter, SafeOwnable {
    using SafeMath for uint;

    address public immutable override factory;
    address public immutable override WETH;
    address public swapMining;

    modifier ensure(uint deadline) {
        require(deadline >= block.timestamp, 'MockRouter: EXPIRED');
        _;
    }

    receive() external payable {
        assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
    }

    function setSwapMining(address _swapMining) public onlyOwner {
        swapMining = _swapMining;
    }

    constructor(address _factory, address _WETH) SafeOwnable(msg.sender) {
        require(_factory != address(0), "illegal address");
        require(_WETH != address(0), "illegal WETH");
        factory = _factory;
        WETH = _WETH;
    }

    // **** ADD LIQUIDITY ****
    function _addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin
    ) internal virtual returns (uint amountA, uint amountB) {
        // create the pair if it doesn't exist yet
        if (IMockFactory(factory).getPair(tokenA, tokenB) == address(0)) {
            IMockFactory(factory).createPair(tokenA, tokenB);
        }
        (uint reserveA, uint reserveB) = MockLibrary.getReserves(factory, tokenA, tokenB);
        if (reserveA == 0 && reserveB == 0) {
            (amountA, amountB) = (amountADesired, amountBDesired);
        } else {
            uint amountBOptimal = MockLibrary.quote(amountADesired, reserveA, reserveB);
            if (amountBOptimal <= amountBDesired) {
                require(amountBOptimal >= amountBMin, 'MockRouter: INSUFFICIENT_B_AMOUNT');
                (amountA, amountB) = (amountADesired, amountBOptimal);
            } else {
                uint amountAOptimal = MockLibrary.quote(amountBDesired, reserveB, reserveA);
                assert(amountAOptimal <= amountADesired);
                require(amountAOptimal >= amountAMin, 'MockRouter: INSUFFICIENT_A_AMOUNT');
                (amountA, amountB) = (amountAOptimal, amountBDesired);
            }
        }
    }
    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external virtual override ensure(deadline) returns (uint amountA, uint amountB, uint liquidity) {
        (amountA, amountB) = _addLiquidity(tokenA, tokenB, amountADesired, amountBDesired, amountAMin, amountBMin);
        address pair = MockLibrary.pairFor(factory, tokenA, tokenB);
        TransferHelper.safeTransferFrom(tokenA, msg.sender, pair, amountA);
        TransferHelper.safeTransferFrom(tokenB, msg.sender, pair, amountB);
        liquidity = IMockPair(pair).mint(to);
    }
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external virtual override payable ensure(deadline) returns (uint amountToken, uint amountETH, uint liquidity) {
        (amountToken, amountETH) = _addLiquidity(
            token,
            WETH,
            amountTokenDesired,
            msg.value,
            amountTokenMin,
            amountETHMin
        );
        address pair = MockLibrary.pairFor(factory, token, WETH);
        TransferHelper.safeTransferFrom(token, msg.sender, pair, amountToken);
        IWETH(WETH).deposit{value: amountETH}();
        assert(IWETH(WETH).transfer(pair, amountETH));
        liquidity = IMockPair(pair).mint(to);
        // refund dust eth, if any
        if (msg.value > amountETH) TransferHelper.safeTransferETH(msg.sender, msg.value - amountETH);
    }

    // **** REMOVE LIQUIDITY ****
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) public virtual override ensure(deadline) returns (uint amountA, uint amountB) {
        address pair = MockLibrary.pairFor(factory, tokenA, tokenB);
        IMockPair(pair).transferFrom(msg.sender, pair, liquidity); // send liquidity to pair
        (uint amount0, uint amount1) = IMockPair(pair).burn(to);
        (address token0,) = MockLibrary.sortTokens(tokenA, tokenB);
        (amountA, amountB) = tokenA == token0 ? (amount0, amount1) : (amount1, amount0);
        require(amountA >= amountAMin, 'MockRouter: INSUFFICIENT_A_AMOUNT');
        require(amountB >= amountBMin, 'MockRouter: INSUFFICIENT_B_AMOUNT');
    }
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) public virtual override ensure(deadline) returns (uint amountToken, uint amountETH) {
        (amountToken, amountETH) = removeLiquidity(
            token,
            WETH,
            liquidity,
            amountTokenMin,
            amountETHMin,
            address(this),
            deadline
        );
        TransferHelper.safeTransfer(token, to, amountToken);
        IWETH(WETH).withdraw(amountETH);
        TransferHelper.safeTransferETH(to, amountETH);
    }
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external virtual override returns (uint amountA, uint amountB) {
        address pair = MockLibrary.pairFor(factory, tokenA, tokenB);
        uint value = approveMax ? uint(-1) : liquidity;
        IMockPair(pair).permit(msg.sender, address(this), value, deadline, v, r, s);
        (amountA, amountB) = removeLiquidity(tokenA, tokenB, liquidity, amountAMin, amountBMin, to, deadline);
    }
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external virtual override returns (uint amountToken, uint amountETH) {
        address pair = MockLibrary.pairFor(factory, token, WETH);
        uint value = approveMax ? uint(-1) : liquidity;
        IMockPair(pair).permit(msg.sender, address(this), value, deadline, v, r, s);
        (amountToken, amountETH) = removeLiquidityETH(token, liquidity, amountTokenMin, amountETHMin, to, deadline);
    }

    // **** REMOVE LIQUIDITY (supporting fee-on-transfer tokens) ****
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) public virtual override ensure(deadline) returns (uint amountETH) {
        (, amountETH) = removeLiquidity(
            token,
            WETH,
            liquidity,
            amountTokenMin,
            amountETHMin,
            address(this),
            deadline
        );
        TransferHelper.safeTransfer(token, to, IERC20(token).balanceOf(address(this)));
        IWETH(WETH).withdraw(amountETH);
        TransferHelper.safeTransferETH(to, amountETH);
    }
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external virtual override returns (uint amountETH) {
        address pair = MockLibrary.pairFor(factory, token, WETH);
        uint value = approveMax ? uint(-1) : liquidity;
        IMockPair(pair).permit(msg.sender, address(this), value, deadline, v, r, s);
        amountETH = removeLiquidityETHSupportingFeeOnTransferTokens(
            token, liquidity, amountTokenMin, amountETHMin, to, deadline
        );
    }

    // **** SWAP ****
    // requires the initial amount to have already been sent to the first pair
    function _swap(uint[] memory amounts, address[] memory path, address _to) internal virtual {
        for (uint i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = MockLibrary.sortTokens(input, output);
            uint amountOut = amounts[i + 1];
            if (swapMining != address(0)) {
                ISwapMining(swapMining).swap(msg.sender, input, output, amountOut);
            }
            (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOut) : (amountOut, uint(0));
            address to = i < path.length - 2 ? MockLibrary.pairFor(factory, output, path[i + 2]) : _to;
            IMockPair(MockLibrary.pairFor(factory, input, output)).swap(
                amount0Out, amount1Out, to, new bytes(0)
            );
        }
    }
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external virtual override ensure(deadline) returns (uint[] memory amounts) {
        amounts = MockLibrary.getAmountsOut(factory, amountIn, path);
        require(amounts[amounts.length - 1] >= amountOutMin, 'MockRouter: INSUFFICIENT_OUTPUT_AMOUNT');
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, MockLibrary.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, to);
    }
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external virtual override ensure(deadline) returns (uint[] memory amounts) {
        amounts = MockLibrary.getAmountsIn(factory, amountOut, path);
        require(amounts[0] <= amountInMax, 'MockRouter: EXCESSIVE_INPUT_AMOUNT');
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, MockLibrary.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, to);
    }
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        virtual
        override
        payable
        ensure(deadline)
        returns (uint[] memory amounts)
    {
        require(path[0] == WETH, 'MockRouter: INVALID_PATH');
        amounts = MockLibrary.getAmountsOut(factory, msg.value, path);
        require(amounts[amounts.length - 1] >= amountOutMin, 'MockRouter: INSUFFICIENT_OUTPUT_AMOUNT');
        IWETH(WETH).deposit{value: amounts[0]}();
        assert(IWETH(WETH).transfer(MockLibrary.pairFor(factory, path[0], path[1]), amounts[0]));
        _swap(amounts, path, to);
    }
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        virtual
        override
        ensure(deadline)
        returns (uint[] memory amounts)
    {
        require(path[path.length - 1] == WETH, 'MockRouter: INVALID_PATH');
        amounts = MockLibrary.getAmountsIn(factory, amountOut, path);
        require(amounts[0] <= amountInMax, 'MockRouter: EXCESSIVE_INPUT_AMOUNT');
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, MockLibrary.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, address(this));
        IWETH(WETH).withdraw(amounts[amounts.length - 1]);
        TransferHelper.safeTransferETH(to, amounts[amounts.length - 1]);
    }
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        virtual
        override
        ensure(deadline)
        returns (uint[] memory amounts)
    {
        require(path[path.length - 1] == WETH, 'MockRouter: INVALID_PATH');
        amounts = MockLibrary.getAmountsOut(factory, amountIn, path);
        require(amounts[amounts.length - 1] >= amountOutMin, 'MockRouter: INSUFFICIENT_OUTPUT_AMOUNT');
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, MockLibrary.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, address(this));
        IWETH(WETH).withdraw(amounts[amounts.length - 1]);
        TransferHelper.safeTransferETH(to, amounts[amounts.length - 1]);
    }
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        virtual
        override
        payable
        ensure(deadline)
        returns (uint[] memory amounts)
    {
        require(path[0] == WETH, 'MockRouter: INVALID_PATH');
        amounts = MockLibrary.getAmountsIn(factory, amountOut, path);
        require(amounts[0] <= msg.value, 'MockRouter: EXCESSIVE_INPUT_AMOUNT');
        IWETH(WETH).deposit{value: amounts[0]}();
        assert(IWETH(WETH).transfer(MockLibrary.pairFor(factory, path[0], path[1]), amounts[0]));
        _swap(amounts, path, to);
        // refund dust eth, if any
        if (msg.value > amounts[0]) TransferHelper.safeTransferETH(msg.sender, msg.value - amounts[0]);
    }

    // **** SWAP (supporting fee-on-transfer tokens) ****
    // requires the initial amount to have already been sent to the first pair
    function _swapSupportingFeeOnTransferTokens(address[] memory path, address _to) internal virtual {
        for (uint i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = MockLibrary.sortTokens(input, output);
            IMockPair pair = IMockPair(MockLibrary.pairFor(factory, input, output));
            uint amountInput;
            uint amountOutput;
            { // scope to avoid stack too deep errors
            (uint reserve0, uint reserve1,) = pair.getReserves();
            (uint reserveInput, uint reserveOutput) = input == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
            amountInput = IERC20(input).balanceOf(address(pair)).sub(reserveInput);
            amountOutput = MockLibrary.getAmountOut(amountInput, reserveInput, reserveOutput);
            }
            if (swapMining != address(0)) {
                ISwapMining(swapMining).swap(msg.sender, input, output, amountOutput);
            }
            (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOutput) : (amountOutput, uint(0));
            address to = i < path.length - 2 ? MockLibrary.pairFor(factory, output, path[i + 2]) : _to;
            pair.swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }
    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external virtual override ensure(deadline) {
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, MockLibrary.pairFor(factory, path[0], path[1]), amountIn
        );
        uint balanceBefore = IERC20(path[path.length - 1]).balanceOf(to);
        _swapSupportingFeeOnTransferTokens(path, to);
        require(
            IERC20(path[path.length - 1]).balanceOf(to).sub(balanceBefore) >= amountOutMin,
            'MockRouter: INSUFFICIENT_OUTPUT_AMOUNT'
        );
    }
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    )
        external
        virtual
        override
        payable
        ensure(deadline)
    {
        require(path[0] == WETH, 'MockRouter: INVALID_PATH');
        uint amountIn = msg.value;
        IWETH(WETH).deposit{value: amountIn}();
        assert(IWETH(WETH).transfer(MockLibrary.pairFor(factory, path[0], path[1]), amountIn));
        uint balanceBefore = IERC20(path[path.length - 1]).balanceOf(to);
        _swapSupportingFeeOnTransferTokens(path, to);
        require(
            IERC20(path[path.length - 1]).balanceOf(to).sub(balanceBefore) >= amountOutMin,
            'MockRouter: INSUFFICIENT_OUTPUT_AMOUNT'
        );
    }
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    )
        external
        virtual
        override
        ensure(deadline)
    {
        require(path[path.length - 1] == WETH, 'MockRouter: INVALID_PATH');
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, MockLibrary.pairFor(factory, path[0], path[1]), amountIn
        );
        _swapSupportingFeeOnTransferTokens(path, address(this));
        uint amountOut = IERC20(WETH).balanceOf(address(this));
        require(amountOut >= amountOutMin, 'MockRouter: INSUFFICIENT_OUTPUT_AMOUNT');
        IWETH(WETH).withdraw(amountOut);
        TransferHelper.safeTransferETH(to, amountOut);
    }

    // **** LIBRARY FUNCTIONS ****
    function quote(uint amountA, uint reserveA, uint reserveB) public pure virtual override returns (uint amountB) {
        return MockLibrary.quote(amountA, reserveA, reserveB);
    }

    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut)
        public
        pure
        virtual
        override
        returns (uint amountOut)
    {
        return MockLibrary.getAmountOut(amountIn, reserveIn, reserveOut);
    }

    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut)
        public
        pure
        virtual
        override
        returns (uint amountIn)
    {
        return MockLibrary.getAmountIn(amountOut, reserveIn, reserveOut);
    }

    function getAmountsOut(uint amountIn, address[] memory path)
        public
        view
        virtual
        override
        returns (uint[] memory amounts)
    {
        return MockLibrary.getAmountsOut(factory, amountIn, path);
    }

    function getAmountsIn(uint amountOut, address[] memory path)
        public
        view
        virtual
        override
        returns (uint[] memory amounts)
    {
        return MockLibrary.getAmountsIn(factory, amountOut, path);
    }
}

